function saveCurrentThemePreset() {
    const currentTheme = detectCurrentTheme();
    const presetName = prompt('è¯·è¾“å…¥é¢„è®¾åç§°:', currentTheme || 'æ–°é¢„è®¾');

    if (!presetName || !presetName.trim()) return;

    const preset = {
        theme: currentTheme,
        settings: getCurrentMultilangSettings(),
        timestamp: new Date().toISOString()
    };
    themePresets[presetName.trim()] = preset;
    saveThemePresets();

    showNotification(`ä¸»é¢˜é¢„è®¾ "${presetName.trim()}" å·²ä¿å­˜ï¼`);
}

function downloadAllPresets() {
    const exportData = {
        version: FONT_PACK_VERSION,
        timestamp: new Date().toISOString(),
        themePresets: themePresets, // ä¸»é¢˜é¢„è®¾
        multilangPresets: multilangPresets, // å¤šè¯­è¨€å­—ä½“é¢„è®¾
        themeBindings: themeMultilangBindings // ä¸»é¢˜ç»‘å®šå…³ç³»
    };

    const jsonString = JSON.stringify(exportData, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = docContext.createElement('a');
    a.href = url;
    const date = new Date().toISOString().slice(0, 10);
    a.download = `all-presets-${date}.json`;
    docContext.body.appendChild(a);
    a.click();
    docContext.body.removeChild(a);
    URL.revokeObjectURL(url);

    showNotification('æ‰€æœ‰é¢„è®¾å·²å¯¼å‡ºï¼åŒ…å«ä¸»é¢˜é¢„è®¾å’Œå­—ä½“é¢„è®¾ã€‚');
}

function uploadAllPresets() {
    const fileInput = docContext.createElement('input');
    fileInput.type = 'file';
    fileInput.accept = '.json';
    fileInput.style.display = 'none';

    fileInput.addEventListener('change', (e) => {
        const file = e.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const data = JSON.parse(e.target.result);

                // å¯¼å…¥ä¸»é¢˜é¢„è®¾
                if (data.themePresets) {
                    Object.assign(themePresets, data.themePresets);
                    saveThemePresets();
                }

                // å¯¼å…¥å¤šè¯­è¨€å­—ä½“é¢„è®¾
                if (data.multilangPresets) {
                    Object.assign(multilangPresets, data.multilangPresets);
                    saveMultilangPresets();
                }

                // å¯¼å…¥ä¸»é¢˜ç»‘å®šå…³ç³»
                if (data.themeBindings) {
                    Object.assign(themeMultilangBindings, data.themeBindings);
                    saveThemeMultilangBindings();
                }

                showNotification('æ‰€æœ‰é¢„è®¾å·²æˆåŠŸå¯¼å…¥ï¼åŒ…å«ä¸»é¢˜é¢„è®¾å’Œå­—ä½“é¢„è®¾ã€‚');
                toggleThemePresetManagementPanel(); // åˆ·æ–°ç®¡ç†é¢æ¿
            } catch (error) {
                showNotification('å¯¼å…¥å¤±è´¥ï¼šæ–‡ä»¶æ ¼å¼é”™è¯¯ï¼', 'error');
                console.error('å¯¼å…¥é¢„è®¾å¤±è´¥:', error);
            }
        };
        reader.readAsText(file);

        docContext.body.removeChild(fileInput);
    });

    docContext.body.appendChild(fileInput);
    fileInput.click();
}

