        // å¼ºåˆ¶æ¸…ç†æ‰€æœ‰å¯èƒ½å­˜åœ¨çš„footeræŒ‰é’®
        const allFooters = modalDialogElement.querySelectorAll('.' + MODAL_FOOTER_CLASS);
        allFooters.forEach(footer => footer.remove());
        modalFooterElement = null;

        return;
    }

    modalElement = docContext.createElement('div');
    modalElement.id = MODAL_ID;
