function _createButton(text, className, onClick) {
    const button = docContext.createElement('button');
    button.textContent = text;
    button.className = 'font-manager-modal-button ' + (className || '').trim();
    button.onclick = onClick;
    return button;
}

function _renderFooterButtons(buttons) {
    if (!modalDialogElement) ensureModalStructure();
    const existingFooter = modalDialogElement.querySelector('.' + MODAL_FOOTER_CLASS);
    if (existingFooter) {
        existingFooter.remove();
    }

    // é‡ç½®footerå…ƒç´ å˜é‡
    modalFooterElement = null;

    if (buttons && buttons.length > 0) {
        modalFooterElement = docContext.createElement('div');
        modalFooterElement.className = MODAL_FOOTER_CLASS;
        buttons.forEach(btnConfig => {
            modalFooterElement.appendChild(_createButton(btnConfig.text, btnConfig.className, btnConfig.onClick));
        });
        modalDialogElement.appendChild(modalFooterElement);
    }
}

// ========= æ‰©å±•èœå•æŒ‰é’®åˆ›å»ºå‡½æ•° =========
function getParentWindow() {
    return window.parent && window.parent !== window ? window.parent : window;
}

function getJQuery() {
    const parentWindow = getParentWindow();
    if (parentWindow.$) {
        return parentWindow.$;
    }
    if (window.$) {
        return window.$;
    }
    throw new Error('jQueryæœªæ‰¾åˆ°');
}

function createExtensionMenuButton() {
    try {
        const $ = getJQuery();
        if ($('#' + MENU_BUTTON_ID).length > 0) {
            console.log('Font Manager: Menu button already exists.');
            return;
        }

        const menuItem = $(`
            <a id="${MENU_BUTTON_ID}" class="list-group-item" href="#" title="å­—ä½“ç®¡ç†">
                <i class="fa-solid fa-font"></i> å­—ä½“ç®¡ç†
            </a>
        `);

        $('#extensionsMenu').append(menuItem);

        menuItem.on('click', (event) => {
            event.preventDefault();
            event.stopPropagation();
            // å…³é—­æ‰©å±•èœå•
            $('#extensionsMenu').fadeOut(200);
            openFontManagerModal();
        });

        console.log('Font Manager: å­—ä½“ç®¡ç†å·¥å…·å·²é›†æˆåˆ°èœå•ï¼');
    } catch (error) {
        console.error('Font Manager: å­—ä½“ç®¡ç†å·¥å…·é›†æˆå¤±è´¥:', error);
    }
}

function waitForExtensionsMenu() {
    try {
        const $ = getJQuery();
        if ($ && $.fn && $('#extensionsMenu').length) {
            setTimeout(createExtensionMenuButton, 1000);
        } else {
            setTimeout(waitForExtensionsMenu, 500);
        }
    } catch (error) {
        console.warn('Font Manager: jQueryæˆ–æ‰©å±•èœå•æœªå°±ç»ªï¼Œç­‰å¾…ä¸­...', error);
        setTimeout(waitForExtensionsMenu, 500);
    }
}

