const CLOUD_FONT_SITES_URL = 'https://sharkpan.xyz/f/YYmVh1/%E5%AD%97%E4%BD%93%E7%BD%91%E7%AB%99.json';

function openRecommendSitesModal() {
    ensureModalStructure();
    modalTitleElement.textContent = 'æ¨èè·å–å­—ä½“ç½‘ç«™';
    modalBodyElement.innerHTML = '';

