function applyColorSetting(settingKey, newColor) {
    const setting = colorSettings[settingKey];
    if (!setting) return;

    // åˆ›å»ºæˆ–æ›´æ–°ä¸“é—¨çš„æ ·å¼æ ‡ç­¾
    let styleId = `font-manager-color-${settingKey}`;
    let styleElement = docContext.getElementById(styleId);

    if (!styleElement) {
        styleElement = docContext.createElement('style');
        styleElement.id = styleId;
        docContext.head.appendChild(styleElement);
    }

    let cssContent = '';

    // ç‰¹æ®Šå¤„ç†å¼ºè°ƒæ–‡æœ¬ï¼ˆå¼•å·å†…å®¹ï¼‰
    if (settingKey === 'accentText') {
        cssContent = `
        /* å¼ºè°ƒæ–‡æœ¬æ ·å¼ - é’ˆå¯¹å¼•å·å†…å®¹çš„è¿‘ä¼¼å¤„ç† */
        .mes_text em,
        .mes_text strong,
        .mes_text b,
        .mes_text i,
        .mes_text [style*="font-style: italic"],
        .mes_text [style*="font-weight: bold"] {
            color: ${newColor} !important;
        }

        /* é¢å¤–çš„å¼•å·æ ‡è¯†å¤„ç† */
        .mes_text *:is(span, p, div):has-text('"') {
            color: ${newColor} !important;
        }

        /* ä½¿ç”¨CSSå˜é‡ä»¥ä¾¿å…¶ä»–åœ°æ–¹å¼•ç”¨ */
        :root {
            --accent-text-color: ${newColor};
        }
        `;
    } else if (settingKey === 'underlineText') {
        // ç‰¹æ®Šå¤„ç†ä¸‹åˆ’çº¿æ–‡æœ¬ï¼Œè®¾ç½®CSSå˜é‡
        cssContent = `
        :root {
            --underline-color: ${newColor};
        }
        ` + (setting.customCSS ? setting.customCSS.replace(/var\(--underline-color[^)]*\)/g, newColor) : 
            setting.cssSelectors.map(selector => 
                `${selector} { ${setting.cssProperty}: ${newColor} !important; }`
            ).join('\n'));
    } else if (settingKey === 'wavyUnderlineText') {
        // ç‰¹æ®Šå¤„ç†æ³¢æµªçº¿æ–‡æœ¬ï¼Œè®¾ç½®CSSå˜é‡
        cssContent = `
        :root {
            --wavy-underline-color: ${newColor};
        }
        ` + (setting.customCSS ? setting.customCSS.replace(/var\(--wavy-underline-color[^)]*\)/g, newColor) : 
            setting.cssSelectors.map(selector => 
                `${selector} { ${setting.cssProperty}: ${newColor} !important; }`
            ).join('\n'));
    } else {
        // æ™®é€šçš„é€‰æ‹©å™¨å¤„ç†
        cssContent = setting.cssSelectors.map(selector => 
            `${selector} { ${setting.cssProperty}: ${newColor} !important; }`
        ).join('\n');
    }

    // å¦‚æœæœ‰è‡ªå®šä¹‰CSSï¼Œä¹Ÿæ·»åŠ è¿›å»ï¼ˆæ’é™¤å·²å¤„ç†çš„ç‰¹æ®Šæƒ…å†µï¼‰
    if (setting.customCSS && !['accentText', 'underlineText', 'wavyUnderlineText'].includes(settingKey)) {
        cssContent += '\n' + setting.customCSS.replace(/var\([^)]+\)/g, newColor);
    }

    styleElement.textContent = cssContent;

    // ä¿å­˜åˆ°localStorage
    localStorage.setItem(setting.lsKey, newColor);
    state[settingKey + 'Color'] = newColor;

    // æ›´æ–°è¾“å…¥æ¡†æ˜¾ç¤º
    const inputElement = docContext.getElementById(setting.inputId);
    if (inputElement) {
        inputElement.value = newColor;
        // æ›´æ–°é¢œè‰²å€¼æ˜¾ç¤º
        const colorValueDisplay = inputElement.parentElement.querySelector('.color-value-display');
        if (colorValueDisplay) {
            colorValueDisplay.textContent = newColor;
        }
    }
}

function resetColorSetting(settingKey) {
    const setting = colorSettings[settingKey];
    if (!setting) return;

    // ç§»é™¤è‡ªå®šä¹‰æ ·å¼
    const styleElement = docContext.getElementById(`font-manager-color-${settingKey}`);
    if (styleElement) {
        styleElement.remove();
    }

    // æ¸…é™¤localStorage
    localStorage.removeItem(setting.lsKey);
    state[settingKey + 'Color'] = setting.initialValue;

    // é‡ç½®è¾“å…¥æ¡†
    const inputElement = docContext.getElementById(setting.inputId);
    if (inputElement) {
        inputElement.value = setting.initialValue;
        // æ›´æ–°é¢œè‰²å€¼æ˜¾ç¤º
        const colorValueDisplay = inputElement.parentElement.querySelector('.color-value-display');
        if (colorValueDisplay) {
            colorValueDisplay.textContent = setting.initialValue;
        }
    }
}

function handleImportLocalFont() {
    const fileInput = docContext.getElementById('fontManagerLocalFontFileInput');

    if (!fileInput) {
        console.error('Font Manager: æ‰¾ä¸åˆ°æœ¬åœ°å­—ä½“å¯¼å…¥æ‰€éœ€çš„è¾“å…¥å…ƒç´ ');
        showNotification('ç•Œé¢å…ƒç´ ç¼ºå¤±ï¼Œæ— æ³•å¯¼å…¥å­—ä½“');
        return;
    }

    const files = fileInput.files;
    if (!files || files.length === 0) {
        showNotification('è¯·å…ˆé€‰æ‹©è‡³å°‘ä¸€ä¸ª .ttf å­—ä½“æ–‡ä»¶ã€‚');
        return;
    }

    let processedCount = 0;
    let successCount = 0;
    const totalFiles = files.length;

    Array.from(files).forEach((file, index) => {
        if (!file.name.toLowerCase().endsWith('.ttf')) {
            processedCount++;
            showNotification(`è·³è¿‡éTTFæ–‡ä»¶: ${file.name}`);
            return;
        }

        if (file.size > 40 * 1024 * 1024) {
            processedCount++;
            showNotification(`å­—ä½“æ–‡ä»¶è¿‡å¤§ï¼Œè·³è¿‡: ${file.name} (è¶…è¿‡40MB)`);
            return;
        }

        const fontFamilyName = file.name.replace(/\.ttf$/i, '');

        if (state.importedFontFamilies.includes(fontFamilyName)) {
            processedCount++;
            showNotification(`å­—ä½“åç§°å·²å­˜åœ¨ï¼Œè·³è¿‡: ${fontFamilyName}`);
            return;
        }

        if (state.localFonts.some(font => font.fontName === fontFamilyName)) {
            processedCount++;
            showNotification(`æœ¬åœ°å­—ä½“å·²å­˜åœ¨ï¼Œè·³è¿‡: ${fontFamilyName}`);
            return;
        }

        const reader = new FileReader();
        reader.onload = function(e) {
            try {
                const fontDataUrl = e.target.result;

                const styleId = `font-manager-local-font-${fontFamilyName.replace(/\s+/g, '-')}`;
                let styleElement = docContext.getElementById(styleId);
                if (styleElement) {
                    styleElement.remove();
                }

                styleElement = docContext.createElement('style');
                styleElement.id = styleId;
                styleElement.textContent = `
                    @font-face {
                        font-family: '${fontFamilyName}';
                        src: url('${fontDataUrl}') format('truetype');
                        font-display: swap;
                    }
                `;
                docContext.head.appendChild(styleElement);

                saveLocalFontToIndexedDB(fontFamilyName, fontDataUrl, file.name)
                    .then(() => {
                        const newFontData = {
                            fontName: fontFamilyName,
                            fontDataUrl: fontDataUrl,
                            fileName: file.name,
                            dateAdded: new Date().toISOString()
                        };

                        state.localFonts.push(newFontData);

                        if (!state.importedFontFamilies.includes(fontFamilyName)) {
                            state.importedFontFamilies.push(fontFamilyName);
                            saveFontsToStorage();
                        }

                        successCount++;
                        processedCount++;

                        if (processedCount === totalFiles) {
                            fileInput.value = '';
                            renderCombinedMainView();
                            showNotification(`å­—ä½“å¯¼å…¥å®Œæˆï¼æˆåŠŸå¯¼å…¥ ${successCount}/${totalFiles} ä¸ªå­—ä½“ã€‚`);
                        }
                    })
                    .catch(error => {
                        console.error('Font Manager: IndexedDBå­˜å‚¨å­—ä½“å¤±è´¥:', error);
                        processedCount++;

                        if (processedCount === totalFiles) {
                            fileInput.value = '';
                            renderCombinedMainView();
                            showNotification(`å­—ä½“å¯¼å…¥å®Œæˆï¼æˆåŠŸå¯¼å…¥ ${successCount}/${totalFiles} ä¸ªå­—ä½“ã€‚`);
                        }
                    });

            } catch (err) {
                console.error('Font Manager: å¤„ç†æœ¬åœ°å­—ä½“æ–‡ä»¶å¤±è´¥:', err);
                processedCount++;

                if (processedCount === totalFiles) {
                    fileInput.value = '';
                    renderCombinedMainView();
                    showNotification(`å­—ä½“å¯¼å…¥å®Œæˆï¼æˆåŠŸå¯¼å…¥ ${successCount}/${totalFiles} ä¸ªå­—ä½“ã€‚`);
                }
            }
        };

        reader.onerror = function(err) {
            console.error('Font Manager: è¯»å–æœ¬åœ°å­—ä½“æ–‡ä»¶å¤±è´¥:', err);
            processedCount++;

            if (processedCount === totalFiles) {
                fileInput.value = '';
                renderCombinedMainView();
                showNotification(`å­—ä½“å¯¼å…¥å®Œæˆï¼æˆåŠŸå¯¼å…¥ ${successCount}/${totalFiles} ä¸ªå­—ä½“ã€‚`);
            }
        };

        reader.readAsDataURL(file);
    });
}

// ========= HLJS Styling Functions =========
function getInitialHljsStyles() {
    const rgbToHex = (rgb) => {
        if (!rgb || typeof rgb !== 'string' || rgb.startsWith('#')) return rgb;
        if (rgb === 'rgba(0, 0, 0, 0)' || rgb === 'transparent') return rgb;

        const result = rgb.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*[\d.]+)?\)$/);
        if (!result) return rgb;
        const r = parseInt(result[1], 10).toString(16).padStart(2, '0');
        const g = parseInt(result[2], 10).toString(16).padStart(2, '0');
        const b = parseInt(result[3], 10).toString(16).padStart(2, '0');
        return `#${r}${g}${b}`;
    };

    // é¦–å…ˆå°è¯•ä»é¡µé¢ç°æœ‰çš„HLJSå…ƒç´ è·å–æ ·å¼
    const existingHljsElements = docContext.querySelectorAll('.hljs, pre code.hljs, code.hljs');
    let useExistingElements = existingHljsElements.length > 0;

    if (useExistingElements) {
        console.log(`Font Manager: æ£€æµ‹åˆ° ${existingHljsElements.length} ä¸ªç°æœ‰HLJSå…ƒç´ ï¼Œç›´æ¥è¯»å–æ ·å¼`);

        // ä»ç¬¬ä¸€ä¸ªç°æœ‰å…ƒç´ è·å–åŸºç¡€æ ·å¼
        const firstElement = existingHljsElements[0];

        Object.values(hljsScopeSettings).forEach(scope => {
            let finalColor = scope.defaultCssValue;

            try {
                if (scope.stateKey === 'hljsForegroundColor' || scope.stateKey === 'hljsBackgroundColor') {
                    // ä»åŸºç¡€.hljså…ƒç´ è·å–é¢œè‰²å’ŒèƒŒæ™¯è‰²
                    const computedStyle = docContext.defaultView.getComputedStyle(firstElement);
                    const colorValue = computedStyle[scope.cssProperty.replace(/-(\w)/g, (match, p1) => p1.toUpperCase())];

                    if (colorValue) {
                        if (scope.cssProperty === 'background-color') {
                            if (colorValue === 'rgba(0, 0, 0, 0)' || colorValue === 'transparent') {
                                finalColor = 'transparent';
                            } else {
                                finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                            }
                        } else {
                            finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                        }
                    }
                } else {
                    // æŸ¥æ‰¾é¡µé¢ä¸­åŒ¹é…çš„å­å…ƒç´ 
                    let foundElement = null;
                    if (scope.selectors && Array.isArray(scope.selectors)) {
                        for (const selector of scope.selectors) {
                            foundElement = docContext.querySelector(selector);
                            if (foundElement) break;
                        }

                        if (foundElement) {
                            const computedStyle = docContext.defaultView.getComputedStyle(foundElement);
                            const colorValue = computedStyle.color;
                            if (colorValue && colorValue !== 'rgba(0, 0, 0, 0)') {
                                finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                            }
                        }
                    }
                }

                state[scope.initialValueKey] = finalColor;
                console.log(`Font Manager: ä»ç°æœ‰å…ƒç´ è¯»å– ${scope.label} æ ·å¼: ${finalColor}`);

            } catch (e) {
                console.warn(`Font Manager: è¯»å–ç°æœ‰å…ƒç´  ${scope.label} æ ·å¼å¤±è´¥ï¼Œä½¿ç”¨é»˜è®¤å€¼`, e);
                state[scope.initialValueKey] = scope.defaultCssValue;
            }
        });

    } else {
        console.log('Font Manager: æœªæ‰¾åˆ°ç°æœ‰HLJSå…ƒç´ ï¼Œåˆ›å»ºä¸´æ—¶å…ƒç´ æ£€æµ‹æ ·å¼');

        // åˆ›å»ºä¸´æ—¶å…ƒç´ æ¥æ£€æµ‹æ ·å¼ï¼ˆä¿ç•™åŸæœ‰é€»è¾‘ä½œä¸ºå¤‡é€‰ï¼‰
        const tempContainer = docContext.createElement('div');
        tempContainer.style.position = 'absolute';
        tempContainer.style.left = '-9999px';
        tempContainer.style.visibility = 'hidden';
        tempContainer.style.zIndex = '-1';

        // åˆ›å»ºä¸€ä¸ªå®Œæ•´çš„HLJSç»“æ„æ¥æ£€æµ‹æ ·å¼
        const tempPre = docContext.createElement('pre');
        const tempCode = docContext.createElement('code');
        tempCode.className = 'hljs';
        tempPre.appendChild(tempCode);
        tempContainer.appendChild(tempPre);

        // ä¸ºæ¯ç§ç±»å‹åˆ›å»ºå­å…ƒç´ 
        const testElements = {};
        Object.values(hljsScopeSettings).forEach(scope => {
            if (scope.stateKey !== 'hljsForegroundColor' && scope.stateKey !== 'hljsBackgroundColor' && scope.selectors && Array.isArray(scope.selectors)) {
                const classes = scope.selectors.join(' ').replace(/\./g, '').split(' ').filter(c => c && c !== 'hljs');
                if (classes.length > 0) {
                    const tempSpan = docContext.createElement('span');
                    tempSpan.className = classes.join(' ');
                    tempSpan.textContent = 'test';
                    tempCode.appendChild(tempSpan);
                    testElements[scope.stateKey] = tempSpan;
                }
            }
        });

        docContext.body.appendChild(tempContainer);

        try {
            Object.values(hljsScopeSettings).forEach(scope => {
                let finalColor = scope.defaultCssValue;

                try {
                    if (scope.stateKey === 'hljsForegroundColor' || scope.stateKey === 'hljsBackgroundColor') {
                        const computedStyle = docContext.defaultView.getComputedStyle(tempCode);
                        const colorValue = computedStyle[scope.cssProperty.replace(/-(\w)/g, (match, p1) => p1.toUpperCase())];

                        if (colorValue) {
                            if (scope.cssProperty === 'background-color') {
                                if (colorValue === 'rgba(0, 0, 0, 0)' || colorValue === 'transparent') {
                                    finalColor = 'transparent';
                                } else {
                                    finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                                }
                            } else {
                                finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                            }
                        }
                    } else if (testElements[scope.stateKey]) {
                        const computedStyle = docContext.defaultView.getComputedStyle(testElements[scope.stateKey]);
                        const colorValue = computedStyle.color;
                        if (colorValue && colorValue !== 'rgba(0, 0, 0, 0)') {
                            finalColor = rgbToHex(colorValue) || scope.defaultCssValue;
                        }
                    } else if (!scope.selectors) {
                        // å¯¹äºæ²¡æœ‰selectorsçš„scopeï¼ˆå¦‚é€‰æ‹©æ¡†ç±»å‹ï¼‰ï¼Œç›´æ¥ä½¿ç”¨é»˜è®¤å€¼
                        finalColor = scope.defaultCssValue;
                    }
                } catch (e) {
                    console.warn(`Font Manager: æ£€æµ‹ä¸´æ—¶å…ƒç´  ${scope.label} æ ·å¼å¤±è´¥ï¼Œä½¿ç”¨é»˜è®¤å€¼`, e);
                }

                state[scope.initialValueKey] = finalColor;
                console.log(`Font Manager: ä»ä¸´æ—¶å…ƒç´ æ£€æµ‹ ${scope.label} æ ·å¼: ${finalColor}`);
            });

        } catch (e) {
            console.warn('Font Manager: ä¸´æ—¶å…ƒç´ æ ·å¼æ£€æµ‹å¤±è´¥ï¼Œä½¿ç”¨é¢„å®šä¹‰é»˜è®¤å€¼', e);
            Object.values(hljsScopeSettings).forEach(scope => {
                state[scope.initialValueKey] = scope.defaultCssValue;
            });
        }

        docContext.body.removeChild(tempContainer);
    }

    // åˆå§‹åŒ–èƒŒæ™¯é€æ˜åº¦
    state.initialHljsBackgroundOpacity = '1.0';

    console.log('Font Manager: HLJS åˆå§‹æ ·å¼æ£€æµ‹å®Œæˆ');
}

function applyHljsStyling(newColorsObject, fromImport = false) {
    Object.values(hljsScopeSettings).forEach(scope => {
        const newValue = newColorsObject[scope.stateKey];

        if (newValue !== undefined) {
            if (scope.isOpacity) {
                // å¤„ç†é€æ˜åº¦ - éœ€è¦ç»“åˆèƒŒæ™¯é¢œè‰²
                const bgColor = state.hljsBackgroundColor || state.initialHljsBackgroundColor;
                const opacity = parseFloat(newValue);
                const rgbaColor = hexToRgba(bgColor, opacity);

                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        // ç¡®ä¿åªåº”ç”¨åˆ°HLJSå…ƒç´ ï¼Œä¸å½±å“mes_text code
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            el.style.setProperty(scope.cssProperty, rgbaColor, 'important');
                        }
                    });
                });
            } else if (scope.isImage) {
                // å¤„ç†èƒŒæ™¯å›¾ç‰‡
                const displayMode = state.hljsBackgroundDisplayMode || 'cover';
                const position = state.hljsBackgroundPosition || 'center';
                const backgroundStyles = getBackgroundStyleFromOptions(newValue, displayMode, position);

                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        // ç¡®ä¿åªåº”ç”¨åˆ°HLJSå…ƒç´ 
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            Object.entries(backgroundStyles).forEach(([property, value]) => {
                                if (value) {
                                    el.style.setProperty(property, value, 'important');
                                } else {
                                    el.style.removeProperty(property);
                                }
                            });
                        }
                    });
                });
            } else {
                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        // ç¡®ä¿åªåº”ç”¨åˆ°HLJSå…ƒç´ 
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            el.style.setProperty(scope.cssProperty, newValue, 'important');
                        }
                    });
                });
            }
            state[scope.stateKey] = newValue;
            if (!fromImport) {
                localStorage.setItem(scope.lsKey, newValue);
            }
        } else if (fromImport) { 
            const initialValueForScope = state[scope.initialValueKey];
            if (scope.isOpacity) {
                const bgColor = state.initialHljsBackgroundColor;
                const opacity = parseFloat(initialValueForScope);
                const rgbaColor = hexToRgba(bgColor, opacity);

                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            el.style.setProperty(scope.cssProperty, rgbaColor, 'important');
                        }
                    });
                });
            } else if (scope.isImage) {
                // é‡ç½®èƒŒæ™¯å›¾ç‰‡
                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            el.style.removeProperty('background-image');
                            el.style.removeProperty('background-size');
                            el.style.removeProperty('background-position');
                            el.style.removeProperty('background-repeat');
                            el.style.removeProperty('background-attachment');
                        }
                    });
                });
            } else {
                scope.selectors.forEach(selector => {
                    const elements = docContext.querySelectorAll(selector);
                    elements.forEach(el => {
                        if (el.classList.contains('hljs') || el.closest('.hljs') || selector === '.hljs') {
                            el.style.setProperty(scope.cssProperty, initialValueForScope, 'important');
                        }
                    });
                });
            }
            state[scope.stateKey] = initialValueForScope;
        }

        const inputElement = docContext.getElementById(scope.inputId);
        if (inputElement) {
            if (scope.isOpacity) {
                inputElement.value = state[scope.stateKey] || state[scope.initialValueKey];
                const valueDisplay = inputElement.nextElementSibling;
                if (valueDisplay) valueDisplay.textContent = (parseFloat(inputElement.value) * 100).toFixed(0) + '%';
            } else if (scope.isImage) {
                inputElement.value = state[scope.stateKey] || '';
            } else {
                inputElement.value = state[scope.stateKey] || state[scope.initialValueKey];
                const valueDisplay = inputElement.nextElementSibling;
                if (valueDisplay) valueDisplay.textContent = inputElement.value;
            }
        }
    });
    console.log('Applied HLJS styling with new colors:', newColorsObject);
}

function resetHljsStyling() {
    // é¦–å…ˆæ¸…é™¤æ‰€æœ‰localStorageä¸­ä¿å­˜çš„HLJSæ ·å¼è®¾ç½®
    Object.values(hljsScopeSettings).forEach(scope => {
        localStorage.removeItem(scope.lsKey);
    });
    localStorage.removeItem(LOCAL_STORAGE_KEY_HLJS_FONT_FAMILY);
    // æ¸…é™¤HLJSèƒŒæ™¯æ˜¾ç¤ºæ–¹å¼å’Œä½ç½®è®¾ç½®
    localStorage.removeItem(LOCAL_STORAGE_KEY_HLJS_BACKGROUND_DISPLAY_MODE);
    localStorage.removeItem(LOCAL_STORAGE_KEY_HLJS_BACKGROUND_POSITION);

    // æ¸…é™¤æ‰€æœ‰åº”ç”¨çš„å†…è”æ ·å¼ï¼ŒåŒ…æ‹¬æ‰€æœ‰å­å…ƒç´ 
    const allHljsElements = docContext.querySelectorAll('.hljs, pre code.hljs, code.hljs, .hljs *, pre code.hljs *, code.hljs *');
    allHljsElements.forEach(el => {
        el.style.removeProperty('font-family');
        el.style.removeProperty('background-image');
        el.style.removeProperty('background-size');
        el.style.removeProperty('background-position');
        el.style.removeProperty('background-repeat');
        el.style.removeProperty('background-attachment');
        el.style.removeProperty('color');
        el.style.removeProperty('background-color');

        // å¦‚æœå…ƒç´ æ²¡æœ‰å…¶ä»–å†…è”æ ·å¼ï¼Œç§»é™¤æ•´ä¸ªstyleå±æ€§
        if (!el.style.cssText.trim()) {
            el.removeAttribute('style');
        }
    });

    // ç§»é™¤å­—ä½“ç®¡ç†æ’ä»¶æ·»åŠ çš„æ‰€æœ‰HLJSç›¸å…³æ ·å¼æ ‡ç­¾
    const fontManagerStyles = docContext.querySelectorAll('style[id*="font-manager"]');
    fontManagerStyles.forEach(style => {
        if (style.textContent.includes('.hljs') || style.textContent.includes('hljs-') || style.id === 'font-manager-hljs-font') {
            style.remove();
        }
    });

    // é‡æ–°æ£€æµ‹çœŸæ­£çš„åˆå§‹æ ·å¼å€¼ï¼ˆç”¨äºç•Œé¢æ˜¾ç¤ºï¼‰
    getInitialHljsStyles();

    // æ¸…ç©ºæ‰€æœ‰çŠ¶æ€ï¼Œè®©å…ƒç´ å›åˆ°CSSåŸå§‹çŠ¶æ€
    Object.values(hljsScopeSettings).forEach(scope => {
        state[scope.stateKey] = null;
    });
    state.hljsFontFamily = null;
    // é‡ç½®HLJSèƒŒæ™¯æ˜¾ç¤ºæ–¹å¼å’Œä½ç½®åˆ°é»˜è®¤å€¼
    state.hljsBackgroundDisplayMode = null;
    state.hljsBackgroundPosition = null;

    // æ›´æ–°æ‰€æœ‰è¾“å…¥æ¡†çš„å€¼ä¸ºé‡æ–°æ£€æµ‹çš„åˆå§‹å€¼
    Object.values(hljsScopeSettings).forEach(scope => {
        const inputElement = docContext.getElementById(scope.inputId);
        if (inputElement) {
            if (scope.isOpacity) {
                inputElement.value = state[scope.initialValueKey];
                const valueDisplay = inputElement.nextElementSibling;
                if (valueDisplay) valueDisplay.textContent = (parseFloat(state[scope.initialValueKey]) * 100).toFixed(0) + '%';
            } else if (scope.isImage) {
                inputElement.value = '';
            } else if (scope.isSelect) {
                // é‡ç½®é€‰æ‹©æ¡†åˆ°é»˜è®¤å€¼
                if (scope.inputId.includes('DisplayMode')) {
                    inputElement.value = 'cover';
                } else if (scope.inputId.includes('Position')) {
                    inputElement.value = 'center';
                }
            } else {
                inputElement.value = state[scope.initialValueKey];
                const valueDisplay = inputElement.nextElementSibling;
                if (valueDisplay) valueDisplay.textContent = state[scope.initialValueKey];
            }
        }
    });

    // ç¡®ä¿æ›´æ–°å­—ä½“é€‰æ‹©å™¨æ˜¾ç¤ºä¸ºé»˜è®¤çŠ¶æ€
    const hljsFontSelector = docContext.getElementById('hljsFontFamilySelector');
    if (hljsFontSelector) {
        hljsFontSelector.value = '';
        // ç¡®ä¿é»˜è®¤é€‰é¡¹è¢«é€‰ä¸­
        const defaultOption = hljsFontSelector.querySelector('option[value=""]');
        if (defaultOption) {
            defaultOption.selected = true;
        }
    }

    console.log('Font Manager: å·²é‡ç½®HLJSæ ·å¼åˆ°çœŸæ­£çš„CSSé»˜è®¤çŠ¶æ€ï¼Œæ¸…é™¤äº†æ‰€æœ‰ä¿å­˜çš„è®¾ç½®');
}

function initializeHljsSettings() {
    // å…ˆæ£€æµ‹åˆå§‹æ ·å¼ç”¨äºæ˜¾ç¤º
    getInitialHljsStyles();

    const loadedColors = {};
    let foundSaved = false;

    // æ£€æŸ¥æ˜¯å¦æœ‰ä¿å­˜çš„æ ·å¼è®¾ç½®
    Object.values(hljsScopeSettings).forEach(scope => {
        const savedColor = localStorage.getItem(scope.lsKey);
        if (savedColor) {
            loadedColors[scope.stateKey] = savedColor;
            state[scope.stateKey] = savedColor;
            foundSaved = true;
        } else {
            // æ²¡æœ‰ä¿å­˜çš„å€¼æ—¶ï¼ŒçŠ¶æ€è®¾ä¸ºnullï¼Œä¸åº”ç”¨ä»»ä½•æ ·å¼
            state[scope.stateKey] = null;
        }
    });

    // åŠ è½½HLJSèƒŒæ™¯æ˜¾ç¤ºæ–¹å¼è®¾ç½®
    const savedHljsDisplayMode = localStorage.getItem(LOCAL_STORAGE_KEY_HLJS_BACKGROUND_DISPLAY_MODE);
    const savedHljsPosition = localStorage.getItem(LOCAL_STORAGE_KEY_HLJS_BACKGROUND_POSITION);

    if (savedHljsDisplayMode) {
        state.hljsBackgroundDisplayMode = savedHljsDisplayMode;
    } else {
        state.hljsBackgroundDisplayMode = null;
    }

    if (savedHljsPosition) {
        state.hljsBackgroundPosition = savedHljsPosition;
    } else {
        state.hljsBackgroundPosition = null;
    }

    // åªæœ‰å½“æ‰¾åˆ°ä¿å­˜çš„è®¾ç½®æ—¶æ‰åº”ç”¨æ ·å¼
    if (foundSaved) {
        applyHljsStyling(loadedColors);
        console.log('Font Manager: åŠ è½½å¹¶åº”ç”¨å·²ä¿å­˜çš„HLJSæ ·å¼è®¾ç½®');
    } else {
        console.log('Font Manager: æœªæ‰¾åˆ°ä¿å­˜çš„HLJSæ ·å¼è®¾ç½®ï¼Œä¿æŒCSSé»˜è®¤çŠ¶æ€');
    }

    // å¤„ç†å­—ä½“è®¾ç½®
    const savedHljsFont = localStorage.getItem(LOCAL_STORAGE_KEY_HLJS_FONT_FAMILY);
    if (savedHljsFont) {
        state.hljsFontFamily = savedHljsFont;
        applyHljsFontFamily(savedHljsFont);
        console.log(`Font Manager: åº”ç”¨å·²ä¿å­˜çš„HLJSå­—ä½“: "${savedHljsFont}"`);
    } else {
        state.hljsFontFamily = null;
        console.log('Font Manager: æœªæ‰¾åˆ°ä¿å­˜çš„HLJSå­—ä½“è®¾ç½®ï¼Œä¿æŒé»˜è®¤');
    }
}

// ========= .mes_text code Styling Functions =========

state.rgbToHex = (colorStr) => {
    if (!colorStr || typeof colorStr !== 'string') return null;
    if (colorStr.startsWith('#')) return colorStr;

    const tempEl = docContext.createElement('div');
    tempEl.style.color = colorStr;
    docContext.body.appendChild(tempEl);
    const computedColor = getComputedStyle(tempEl).color;
    docContext.body.removeChild(tempEl);

    const match = computedColor.match(/^rgb\((\d+),\s*(\d+),\s*(\d+)\)$/);
    if (match) {
        const r = parseInt(match[1], 10).toString(16).padStart(2, '0');
        const g = parseInt(match[2], 10).toString(16).padStart(2, '0');
        const b = parseInt(match[3], 10).toString(16).padStart(2, '0');
        return `#${r}${g}${b}`;
    }
    return colorStr;
};

function getInitialMessageInlineCodeStyles() {
    const rgbToHex = (rgb) => {
        if (!rgb || typeof rgb !== 'string') return null;
        if (rgb.startsWith('#')) return rgb;
        if (rgb === 'rgba(0, 0, 0, 0)' || rgb === 'transparent') return 'transparent';

        const result = rgb.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)$/);
        if (!result) return null;

        // æ£€æŸ¥é€æ˜åº¦
        const alpha = result[4] ? parseFloat(result[4]) : 1;
        if (alpha === 0) return 'transparent';

        const r = parseInt(result[1], 10).toString(16).padStart(2, '0');
        const g = parseInt(result[2], 10).toString(16).padStart(2, '0');
        const b = parseInt(result[3], 10).toString(16).padStart(2, '0');
        return `#${r}${g}${b}`;
    };

    // è®¾ç½®é»˜è®¤çš„å®‰å…¨å›é€€å€¼
    let defaultBgColor = 'transparent';
    let defaultTextColor = '#d19a66';
    let defaultBorderColor = '#d2b48c';

    // é¦–å…ˆå°è¯•ä»é¡µé¢ç°æœ‰çš„ .mes_text code å…ƒç´ è·å–æ ·å¼
    const existingMesCodeElements = docContext.querySelectorAll('.mes_text code');

    if (existingMesCodeElements.length > 0) {
        console.log(`Font Manager: æ£€æµ‹åˆ° ${existingMesCodeElements.length} ä¸ªç°æœ‰.mes_text codeå…ƒç´ ï¼Œç›´æ¥è¯»å–æ ·å¼`);

        // ä»ç¬¬ä¸€ä¸ªç°æœ‰å…ƒç´ è·å–æ ·å¼
        const firstElement = existingMesCodeElements[0];

        try {
            const computedStyle = docContext.defaultView.getComputedStyle(firstElement);

            const bgColor = computedStyle.backgroundColor;
            const textColor = computedStyle.color;
            const borderColor = computedStyle.borderColor || computedStyle.borderTopColor || computedStyle.borderLeftColor;

            // å¤„ç†èƒŒæ™¯è‰²
            const convertedBgColor = rgbToHex(bgColor);
            state.initialMesCodeBgColor = convertedBgColor || defaultBgColor;

            // å¤„ç†æ–‡æœ¬è‰²
            const convertedTextColor = rgbToHex(textColor);
            state.initialMesCodeTextColor = convertedTextColor || defaultTextColor;

            // å¤„ç†è¾¹æ¡†è‰²
            const convertedBorderColor = rgbToHex(borderColor);
            state.initialMesCodeBorderColor = convertedBorderColor || defaultBorderColor;

            console.log(`Font Manager: ä»ç°æœ‰å…ƒç´ è¯»å–.mes_text codeæ ·å¼ - BG: ${state.initialMesCodeBgColor}, Text: ${state.initialMesCodeTextColor}, Border: ${state.initialMesCodeBorderColor}`);

        } catch (e) {
            console.warn('Font Manager: è¯»å–ç°æœ‰.mes_text codeå…ƒç´ æ ·å¼å¤±è´¥ï¼Œä½¿ç”¨é»˜è®¤å€¼', e);
            state.initialMesCodeBgColor = defaultBgColor;
            state.initialMesCodeTextColor = defaultTextColor;
            state.initialMesCodeBorderColor = defaultBorderColor;
        }

    } else {
        console.log('Font Manager: æœªæ‰¾åˆ°ç°æœ‰.mes_text codeå…ƒç´ ï¼Œåˆ›å»ºçº¯å‡€ä¸´æ—¶å…ƒç´ æ£€æµ‹ä¸»é¢˜æ ·å¼');

        // åˆ›å»ºå®Œå…¨çº¯å‡€çš„ä¸´æ—¶å…ƒç´ æ¥æ£€æµ‹ä¸»é¢˜çš„çœŸå®æ ·å¼
        const tempContainer = docContext.createElement('div');
