function generatePresetName() {
    const fontNames = [];
    Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
        const fontFamily = state[langConfig.stateKey];
        if (fontFamily && fontFamily.trim()) {
            const cleanName = fontFamily.replace(/\s*\[(TTF|Import)\]\s*/g, '');
            fontNames.push(cleanName);
        }
    });
    return fontNames.join('') || 'æœªå‘½åé¢„è®¾';
}

