function applyMultilangPreset(presetName, isAutoApply = false) {
    const preset = multilangPresets[presetName];
    if (!preset) {
        showNotification(`é¢„è®¾ "${presetName}" ä¸å­˜åœ¨ï¼`);
        return;
    }

    state.multilangEnabled = preset.enabled || false;

    Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
        if (preset.fonts[langKey]) {
            state[langConfig.stateKey] = preset.fonts[langKey].fontFamily || null;
            state[langConfig.urlKey] = preset.fonts[langKey].fontUrl || null;
        }
    });

    // è®¾ç½®ä¸ºå¤šè¯­è¨€åº”ç”¨æ¨¡å¼
    state.fontApplicationMode = 'multilang';
    state.singleFontFamily = null;
    localStorage.setItem('fontManagerApplicationMode', 'multilang');
    localStorage.removeItem('fontManagerSingleFont');

    saveMultilangSettings();
    applyMultilangCSS();

