        // è‡ªåŠ¨åº”ç”¨æ—¶çš„ç‰¹æ®Šæé†’
        const currentThemeName = currentTheme || 'å½“å‰ä¸»é¢˜';
        showNotification(`å·²åº”ç”¨ä¸»é¢˜é¢„è®¾: "${currentThemeName}+${presetName}"`, 'info', 'ä¸»é¢˜ç»‘å®šå­—ä½“é¢„è®¾è‡ªåŠ¨åˆ‡æ¢');
    } else {
        // æ‰‹åŠ¨åº”ç”¨æ—¶çš„æ™®é€šæé†’
        showNotification(`å¤šè¯­è¨€å­—ä½“é¢„è®¾ "${presetName}" å·²åº”ç”¨ï¼`);
    }
}

