        }
    });

    // åœ¨åº”ç”¨ä»»ä½•æ ·å¼ä¹‹å‰å…ˆè·å–çœŸæ­£çš„åˆå§‹æ ·å¼
    getInitialHljsStyles();

    // å…ˆåˆå§‹åŒ–HLJSè®¾ç½®
    initializeHljsSettings();

    // å»¶è¿Ÿåˆå§‹åŒ–æ¶ˆæ¯å†…è”ä»£ç è®¾ç½®ï¼Œç¡®ä¿ä¸ä¼šè¦†ç›–HLJS
    setTimeout(() => {
