        localStorage.removeItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
    }

    injectStyles();
    waitForExtensionsMenu();

    loadLocalFontsFromIndexedDB()
        .then(() => {
            if (state.localFonts && state.localFonts.length > 0) {
                console.log(`Font Manager: ä»IndexedDBåŠ è½½äº†${state.localFonts.length}ä¸ªæœ¬åœ°å­—ä½“ï¼Œå¼€å§‹åº”ç”¨åˆ°é¡µé¢`);
                applyLocalFontsToPage();
                console.log('Font Manager: æœ¬åœ°å­—ä½“å·²ä» IndexedDB åŠ è½½å¹¶åº”ç”¨');
            } else {
                console.log('Font Manager: æ²¡æœ‰ä»IndexedDBåŠ è½½åˆ°æœ¬åœ°å­—ä½“');
            }
        })
        .catch(error => {
            console.error('Font Manager: åŠ è½½æŒä¹…åŒ–å­˜å‚¨çš„æœ¬åœ°å­—ä½“å¤±è´¥:', error);
        });

    if (state.multilangEnabled) {
        console.log('Font Manager: å¤šè¯­è¨€å­—ä½“è®¾ç½®å·²å¯ç”¨');
        applyMultilangCSS();
    } else {
