    }

    console.log('Font Manager initialized.');
    setupDynamicContentObserver();

    // åˆå§‹åŒ–ä¸»é¢˜é¢„è®¾
    loadThemePresets();
    loadMultilangPresets();
    loadThemeMultilangBindings();

    // å»¶è¿Ÿåˆå§‹åŒ–ä¸»é¢˜æ£€æµ‹ï¼Œç¡®ä¿é¡µé¢å…ƒç´ å®Œå…¨åŠ è½½
    setTimeout(() => {
        currentTheme = detectCurrentTheme();
