        $content[isExpanded ? "hide" : "show"](),
        (function saveGroupFoldState(areaKey, groupName, isFolded) {
          const storageKey = `script_group_fold_state_${areaKey}`,
            foldStates = JSON.parse(localStorage.getItem(storageKey) || "{}");
