            localStorage.setItem(storageKey, JSON.stringify(foldStates));
        })(areaKey, thisGroupName, isExpanded);
    },
    { minDelay: 200, operationName: `åˆ‡æ¢åˆ†ç»„(${areaKey})` }
  );
}
