function detectGroupFromScriptName(scriptName) {
  if (!scriptName || typeof scriptName !== 'string') return null;

  // æŒ‰ä¼˜å…ˆçº§é¡ºåºåŒ¹é…åˆ†ç»„æ¨¡å¼
  const match = scriptName.match(config_CONFIG.GROUP_PREFIX_REGEX);

