            } catch (error) {
                document.getElementById('regexes-list').innerHTML = `<div class="empty-state">åŠ è½½å¤±è´¥: ${error.message}</div>`;
                debugLog('æ­£åˆ™åŠ è½½é”™è¯¯: ' + error.stack);
            }
        }

        async function loadQuickReplies() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('éœ€è¦åœ¨SillyTavernç¯å¢ƒä¸­è¿è¡Œ');
                }
                const qrSetListJson = await TavernHelper.triggerSlash('/qr-set-list all');
                const qrSetNames = JSON.parse(qrSetListJson);
                debugLog(`æ‰¾åˆ° ${qrSetNames.length} ä¸ªå¿«é€Ÿå›å¤é›†`);

                const container = document.getElementById('quick-reply-list');
                container.innerHTML = '';

                if (qrSetNames.length === 0) {
                    container.innerHTML = '<div class="empty-state">æœªæ‰¾åˆ°å¿«é€Ÿå›å¤é›†</div>';
                    return;
                }

                for (const name of qrSetNames) {
                    const qrListJson = await TavernHelper.triggerSlash(`/qr-list "${name}"`);
                    const qrList = JSON.parse(qrListJson);

                    const itemDiv = document.createElement('div');
