    //ã€90ä¿®æ”¹ã€‘æ‰‹åŠ¨æ‰§è¡Œè‡ªåŠ¨æ€»ç»“çš„é€»è¾‘
    async function handleAutoSummarize() {
        if (isAutoSummarizing) {
            showToastr("info", "è‡ªåŠ¨æ€»ç»“å·²åœ¨è¿›è¡Œä¸­...");
            return;
        }
        const effectiveChunkSize = getEffectiveChunkSize("handleAutoSummarize_UI");
        // --- NEW TRIGGER LOGIC: N + X ---
        const triggerThreshold = effectiveChunkSize + currentVisibilityOffset;

        logDebug(`HandleAutoSummarize: ä½¿ç”¨é—´éš”(N): ${effectiveChunkSize}, è§¦å‘é˜ˆå€¼(N+X): ${triggerThreshold}`);
        isAutoSummarizing = true;
        if ($autoSummarizeButton) $autoSummarizeButton.prop('disabled', true).text("è‡ªåŠ¨æ€»ç»“ä¸­...");
        if ($statusMessageSpan) $statusMessageSpan.text(`å¼€å§‹è‡ªåŠ¨æ€»ç»“ (é—´éš” ${effectiveChunkSize} å±‚, é˜ˆå€¼ ${triggerThreshold} å±‚)...`);
        else showToastr("info", `å¼€å§‹è‡ªåŠ¨æ€»ç»“ (é—´éš” ${effectiveChunkSize} å±‚, é˜ˆå€¼ ${triggerThreshold} å±‚)...`);

        try {
            let maxSummarizedFloor = await getMaxSummarizedFloorFromActiveLorebookEntry();
            let nextChunkStartFloor = maxSummarizedFloor + 1;
            if (allChatMessages.length === 0) { await loadAllChatMessages(); }
            if (allChatMessages.length === 0) {
                 showToastr("info", "æ²¡æœ‰èŠå¤©è®°å½•å¯æ€»ç»“ã€‚");
                 if($statusMessageSpan) $statusMessageSpan.text("æ²¡æœ‰èŠå¤©è®°å½•ã€‚");
                 isAutoSummarizing = false;
                 if($autoSummarizeButton) $autoSummarizeButton.prop('disabled', false).text("å¼€å§‹/ç»§ç»­è‡ªåŠ¨æ€»ç»“");
                 return;
            }

            let unsummarizedCount = allChatMessages.length - (maxSummarizedFloor + 1);

            // Check for the very first summarization run
            if (maxSummarizedFloor === -1 && unsummarizedCount < triggerThreshold) {
                showToastr("info", `æ€»æ¥¼å±‚æ•° (${unsummarizedCount}) å°äºé¦–æ¬¡è§¦å‘é˜ˆå€¼ (${triggerThreshold})ï¼Œä¸è¿›è¡Œè‡ªåŠ¨æ€»ç»“ã€‚`);
                if($statusMessageSpan) $statusMessageSpan.text(`æ¥¼å±‚æ•°ä¸è¶³ ${triggerThreshold}ã€‚`);
                isAutoSummarizing = false;
                if($autoSummarizeButton) $autoSummarizeButton.prop('disabled', false).text("å¼€å§‹/ç»§ç»­è‡ªåŠ¨æ€»ç»“");
                return;
            }

            logDebug(`è‡ªåŠ¨æ€»ç»“ï¼šå·²æ€»ç»“åˆ° ${maxSummarizedFloor + 1} æ¥¼ã€‚å‰©ä½™æœªæ€»ç»“ ${unsummarizedCount} æ¥¼ã€‚ä¸‹æ¬¡åŒºå—å¤§å° ${effectiveChunkSize}ã€‚è§¦å‘é˜ˆå€¼ ${triggerThreshold}`);

            while (unsummarizedCount >= triggerThreshold) {
                logDebug(`è‡ªåŠ¨æ€»ç»“å¾ªç¯ï¼šå‡†å¤‡å¤„ç†åŒºå— (æœªæ€»ç»“ ${unsummarizedCount} >= é˜ˆå€¼ ${triggerThreshold})ã€‚å½“å‰ nextChunkStartFloor (0-based): ${nextChunkStartFloor}, åŒºå—å¤§å°: ${effectiveChunkSize}`);
                const currentStatusText = `æ­£åœ¨æ€»ç»“ ${nextChunkStartFloor + 1} è‡³ ${nextChunkStartFloor + effectiveChunkSize} æ¥¼...`;
                if($statusMessageSpan) $statusMessageSpan.text(currentStatusText); else showToastr("info", currentStatusText);

                const success = await summarizeAndUploadChunk(nextChunkStartFloor, nextChunkStartFloor + effectiveChunkSize - 1);
                 if (!success) {
                    showToastr("error", `è‡ªåŠ¨æ€»ç»“åœ¨åŒºå— ${nextChunkStartFloor + 1}-${nextChunkStartFloor + effectiveChunkSize} å¤±è´¥ï¼Œå·²åœæ­¢ã€‚`);
                    throw new Error(`è‡ªåŠ¨æ€»ç»“åŒºå— ${nextChunkStartFloor + 1}-${nextChunkStartFloor + effectiveChunkSize} å¤±è´¥ã€‚`);
                }

                // Recalculate state after a successful chunk
                maxSummarizedFloor += effectiveChunkSize;
                nextChunkStartFloor += effectiveChunkSize;
                unsummarizedCount -= effectiveChunkSize;

                await applyPersistedSummaryStatusFromLorebook(); // This is good practice but our manual tracking is faster
                updateUIDisplay();
                logDebug(`è‡ªåŠ¨æ€»ç»“ï¼šå·²æ€»ç»“åˆ° ${maxSummarizedFloor + 1} æ¥¼ã€‚å‰©ä½™æœªæ€»ç»“ ${unsummarizedCount} æ¥¼ã€‚`);
                await new Promise(resolve => setTimeout(resolve, 500));
            }

            const finalStatusText = unsummarizedCount > 0 ?
                `è‡ªåŠ¨æ€»ç»“å®Œæˆã€‚å‰©ä½™ ${unsummarizedCount} æ¥¼æœªè¾¾åˆ°è§¦å‘é˜ˆå€¼ (${triggerThreshold})ã€‚` :
                "æ‰€æœ‰èŠå¤©è®°å½•å·²è‡ªåŠ¨æ€»ç»“å®Œæ¯•ï¼";
            showToastr(unsummarizedCount === 0 ? "success" : "info", finalStatusText);
            if($statusMessageSpan) $statusMessageSpan.text(finalStatusText);
        } catch (error) {
            logError("è‡ªåŠ¨æ€»ç»“è¿‡ç¨‹ä¸­å‘ç”Ÿé”™è¯¯:", error);
            showToastr("error", "è‡ªåŠ¨æ€»ç»“å¤±è´¥: " + error.message);
            if($statusMessageSpan) $statusMessageSpan.text("è‡ªåŠ¨æ€»ç»“å‡ºé”™ã€‚");
        } finally {
            isAutoSummarizing = false;
            if($autoSummarizeButton) $autoSummarizeButton.prop('disabled', false).text("å¼€å§‹/ç»§ç»­è‡ªåŠ¨æ€»ç»“");
        }
    }
