        }

        .content {
            position: relative;
            height: 100%;
            display: flex;
            align-items: center;
            padding: 20px;
            gap: 20px;
        }

        .vinyl {
            width: 60px;
            height: 60px;
            min-width: 60px;
            background: radial-gradient(circle at 30% 30%, 
                #3a3a3a, #1a1a1a 30%, #0a0a0a 50%, #2a2a2a);
            border-radius: 50%;
            box-shadow: 0 4px 15px rgba(0,0,0,0.8);
            cursor: pointer;
            position: relative;
            transition: transform 0.3s;
        }

        .vinyl:hover {
            transform: scale(1.1);
        }

        .vinyl.spin {
            animation: rotate 3s linear infinite;
        }

        @keyframes rotate {
            to { transform: rotate(360deg); }
        }

        .vinyl::before {
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 22px;
            height: 22px;
            background: radial-gradient(circle, #90ee90, #3cb371);
            border-radius: 50%;
