        <!-- 开场白列表 -->
        <section class="content-section">
            <h2 class="section-title">◇ 开启故事 ◇</h2>
            <div class="opening-list" id="opening-list">
                <!-- 此处将由JavaScript动态生成开场白列表 -->
            </div>
        </section>

    </div>

    <script>
        // ===== 使用说明弹窗功能 =====
        const openHelpBtn = document.getElementById('open-help');
        const closeModalBtn = document.getElementById('close-modal');
        const modalOverlay = document.getElementById('modal-overlay');

        openHelpBtn.addEventListener('click', () => {
            modalOverlay.classList.add('active');
        });

        closeModalBtn.addEventListener('click', () => {
            modalOverlay.classList.remove('active');
        });

        modalOverlay.addEventListener('click', (e) => {
            if (e.target === modalOverlay) {
                modalOverlay.classList.remove('active');
            }
        });

        // ===== 日记翻页功能 =====
        let currentPage = 1;
        const totalPages = 5;
        const pages = document.querySelectorAll('.diary-page');
        const dots = document.querySelectorAll('.page-dot');
        const prevBtn = document.getElementById('prev-page');
        const nextBtn = document.getElementById('next-page');

        function showPage(pageNum) {
            // 隐藏所有页面
            pages.forEach(page => page.classList.remove('active'));
            dots.forEach(dot => dot.classList.remove('active'));

            // 显示目标页面
            const targetPage = document.querySelector(`.diary-page[data-page="${pageNum}"]`);
            const targetDot = document.querySelector(`.page-dot[data-page="${pageNum}"]`);

            if (targetPage) targetPage.classList.add('active');
            if (targetDot) targetDot.classList.add('active');

            // 更新按钮状态
            prevBtn.disabled = (pageNum === 1);
            nextBtn.disabled = (pageNum === totalPages);

            currentPage = pageNum;
        }

        prevBtn.addEventListener('click', () => {
            if (currentPage > 1) {
                showPage(currentPage - 1);
            }
        });

        nextBtn.addEventListener('click', () => {
            if (currentPage < totalPages) {
                showPage(currentPage + 1);
            }
        });

        dots.forEach(dot => {
            dot.addEventListener('click', () => {
                const pageNum = parseInt(dot.getAttribute('data-page'));
                showPage(pageNum);
            });
        });

        // 初始化第一页
        showPage(1);

        // ===== 开场白动态加载功能 =====
        async function loadOpenings() {
            const container = document.getElementById('opening-list');

            // 检查SillyTavern API是否可用
            if (typeof getChatMessages !== 'function') {
                container.innerHTML = '<p style="text-align: center; color: var(--text-secondary);">无法加载开场白列表，请确保在SillyTavern环境中使用。</p>';
                return;
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });

                // 检查数据有效性
                if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    container.innerHTML = '<p style="text-align: center; color: var(--text-secondary);">暂无额外开场白。请添加更多开场白以查看列表。</p>';
                    return;
                }

                const swipes = messages[0].swipes;

                // 从索引1开始遍历（索引0是当前主页）
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `开场白 ${i}`;
                    let description = "点击开启新的故事线。";

                    // 使用正则表达式解析元数据
                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
                    if (titleMatch) title = titleMatch[1].trim();

                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
                    if (descMatch) description = descMatch[1].trim();

                    // 创建开场白卡片
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.innerHTML = `
                        <div class="opening-title">${title}</div>
                        <div class="opening-desc">${description}</div>
                    `;

                    // 绑定点击事件
                    item.addEventListener('click', () => switchToOpening(i));

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白失败:', error);
                container.innerHTML = '<p style="text-align: center; color: #a98e6f;">加载开场白时出错，请查看控制台获取详细信息。</p>';
            }
        }

        async function switchToOpening(swipeId) {
            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                } else {
                    throw new Error(`开场白 ${swipeId} 不存在`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                alert(`切换失败：${error.message}`);
            }
        }

        // 页面加载完成后执行
        window.addEventListener('load', loadOpenings);

        // ===== 互动气泡点击效果 =====
        const interactiveBubbles = document.querySelectorAll('.interactive-bubble');
        interactiveBubbles.forEach(bubble => {
            bubble.addEventListener('click', function() {
                // 创建涟漪效果
                const ripple = document.createElement('span');
                ripple.style.position = 'absolute';
                ripple.style.width = '40px';
                ripple.style.height = '40px';
                ripple.style.borderRadius = '50%';
                ripple.style.background = 'rgba(139, 154, 109, 0.3)';
                ripple.style.transform = 'scale(0)';
                ripple.style.animation = 'ripple 0.6s ease-out';
                ripple.style.pointerEvents = 'none';
                ripple.style.left = '50%';
                ripple.style.top = '50%';
                ripple.style.marginLeft = '-20px';
                ripple.style.marginTop = '-20px';

                this.style.position = 'relative';
                this.appendChild(ripple);

                setTimeout(() => ripple.remove(), 600);
            });
        });

        // 添加涟漪动画
        const style = document.createElement('style');
        style.textContent = `
            @keyframes ripple {
                to {
                    transform: scale(2);
                    opacity: 0;
                }
            }
        `;
        document.head.appendChild(style);

    </script>
</body>
</html>
