        overflow: hidden;
        position: relative;
    }

    .lq-vinyl-container {
        flex-shrink: 0;
        position: relative;
        width: 100px;
        height: 100px;
        margin-left: 5px;
    }

    .lq-vinyl-disc {
        width: 100%;
        height: 100%;
        border-radius: 50%;
        background: radial-gradient(circle, #d1c5b6 18%, #4a4a4a 19%, #3c3c3c 70%, #4a4a4a 71%);
        box-shadow:
            inset 0 0 3px rgba(0,0,0,0.5),
            0 0 0 1px #333,
            0 0 0 2px #555,
            0 0 8px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        transform-style: preserve-3d;
    }

    .lq-vinyl-disc::before {
        content: '';
        position: absolute;
        width: 100%;
        height: 100%;
        border-radius: 50%;
        background-image: repeating-radial-gradient(rgba(255,255,255,0.02) 0px, rgba(255,255,255,0.02) 1px, transparent 1px, transparent 3px);
        opacity: 0.8;
    }

    .lq-vinyl-disc.is-playing {
