            0 0 0 2px #555,
            0 0 8px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
        transform-style: preserve-3d;
    }

    .lq-vinyl-disc::before {
        content: '';
        position: absolute;
        width: 100%;
        height: 100%;
        border-radius: 50%;
        background-image: repeating-radial-gradient(rgba(255,255,255,0.02) 0px, rgba(255,255,255,0.02) 1px, transparent 1px, transparent 3px);
        opacity: 0.8;
    }

    .lq-vinyl-disc.is-playing {
        animation: lq-spin 4s linear infinite;
    }

    @keyframes lq-spin {
        from { transform: rotate(0deg); }
        to { transform: rotate(360deg); }
    }

    .lq-tonearm {
        position: absolute;
        top: -8px;
        left: -5px;
        width: 60px;
        height: 15px;
        transform-origin: 10% 50%;
        transform: rotate(35deg);
        transition: transform 0.4s cubic-bezier(0.25, 0.1, 0.25, 1);
        z-index: 10;
    }
    .lq-tonearm::before {
        content: '';
        position: absolute;
        top: 50%;
        right: 0;
        width: 85%;
        height: 4px;
        background: linear-gradient(to left, #c0b2a3, #d1c5b6);
        border-radius: 2px;
        box-shadow: 0 1px 2px rgba(0,0,0,0.2);
        transform: translateY(-50%);
    }
    .lq-tonearm::after {
        content: '';
        position: absolute;
        top: 50%;
        left: 0;
        width: 12px;
        height: 12px;
        background: #a89d91;
        border: 2px solid #f7f3ed;
        border-radius: 50%;
        box-shadow: 0 0 5px rgba(0,0,0,0.3);
        transform: translateY(-50%);
    }
    .lq-tonearm.is-playing {
        transform: rotate(0deg);
    }

