</div>

<script>
    document.addEventListener('DOMContentLoaded', () => {
        const audioPlayer = document.getElementById('lq-audio-player');
        const vinylDisc = document.getElementById('lq-vinyl-disc');
        const tonearm = document.getElementById('lq-tonearm');
        const songInfo = document.getElementById('lq-song-info');
        const playPauseBtn = document.getElementById('lq-play-pause-btn');
        const playIcon = document.getElementById('lq-play-icon');
        const pauseIcon = document.getElementById('lq-pause-icon');
        const prevBtn = document.getElementById('lq-prev-btn');
        const nextBtn = document.getElementById('lq-next-btn');

        const playlist = [
            { "title": "半点心", "url": "http://music.163.com/song/media/outer/url?id=1397664169.mp3" }
        ];
        let currentSongIndex = 0;

        function loadSong(song) {
            songInfo.textContent = song.title;
            songInfo.title = song.title;
            audioPlayer.src = song.url;
        }

        function playSong() {
            const playPromise = audioPlayer.play();
            if (playPromise !== undefined) {
                playPromise.then(_ => {
                    tonearm.classList.add('is-playing');
                    vinylDisc.classList.add('is-playing');
                    playIcon.classList.add('lq-hidden');
                    pauseIcon.classList.remove('lq-hidden');
                }).catch(error => {
                    console.log("Autoplay was prevented. User interaction is needed to start playback.");
                    // Keep UI in paused state if autoplay fails
                    pauseSongVisuals();
                });
            }
        }

        function pauseSong() {
            audioPlayer.pause();
            pauseSongVisuals();
        }

        function pauseSongVisuals() {
            tonearm.classList.remove('is-playing');
            vinylDisc.classList.remove('is-playing');
            playIcon.classList.remove('lq-hidden');
            pauseIcon.classList.add('lq-hidden');
        }

        playPauseBtn.addEventListener('click', () => {
             if (audioPlayer.paused) {
                 playSong();
             } else {
                 pauseSong();
             }
        });

        prevBtn.addEventListener('click', () => {
            currentSongIndex = (currentSongIndex - 1 + playlist.length) % playlist.length;
            loadSong(playlist[currentSongIndex]);
            playSong();
        });

        nextBtn.addEventListener('click', () => {
            currentSongIndex = (currentSongIndex + 1) % playlist.length;
            loadSong(playlist[currentSongIndex]);
            playSong();
        });

        audioPlayer.addEventListener('ended', () => {
             nextBtn.click();
        });

        // Initialize and attempt to autoplay
        if (playlist && playlist.length > 0) {
            loadSong(playlist[currentSongIndex]);
            playSong(); // Attempt to autoplay on load
        }
    });
</script>

