                if (!e.target.closest('.font-size-control') && !e.target.closest('.menu-toggle')) {
                    fontControls.forEach(control => {
                        control.classList.remove('show');
                    });
                }
            });

            // ä¿å­˜å­—ä½“å¤§å°åˆ°æœ¬åœ°å­˜å‚¨
            function saveFontSize(size) {
                localStorage.setItem('card-button-font-size', size);
            }

            // ä»æœ¬åœ°å­˜å‚¨è¯»å–å­—ä½“å¤§å°
            function loadFontSize() {
                const savedSize = localStorage.getItem('card-button-font-size');
                if (savedSize) {
                    currentFontSize = parseInt(savedSize);
                    // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                    fontSizeDisplays.forEach(display => {
                        display.textContent = currentFontSize;
                    });
                    document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                }
            }

            // ä¸ºæ‰€æœ‰å‡å°å­—ä½“æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶
            decreaseBtns.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    e.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡
                    if (currentFontSize > 10) { // é™åˆ¶æœ€å°å­—ä½“å¤§å°
                        currentFontSize -= 1;
                        // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                        fontSizeDisplays.forEach(display => {
                            display.textContent = currentFontSize;
                        });
                        document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                        saveFontSize(currentFontSize);
                    }
                });
            });

            // ä¸ºæ‰€æœ‰å¢å¤§å­—ä½“æŒ‰é’®æ·»åŠ ç‚¹å‡»äº‹ä»¶
            increaseBtns.forEach(btn => {
                btn.addEventListener('click', (e) => {
                    e.stopPropagation(); // é˜»æ­¢äº‹ä»¶å†’æ³¡
                    if (currentFontSize < 24) { // é™åˆ¶æœ€å¤§å­—ä½“å¤§å°
                        currentFontSize += 1;
                        // æ›´æ–°æ‰€æœ‰æ˜¾ç¤ºå­—ä½“å¤§å°çš„å…ƒç´ 
                        fontSizeDisplays.forEach(display => {
                            display.textContent = currentFontSize;
                        });
                        document.documentElement.style.setProperty('--button-font-size', `${currentFontSize}px`);
                        saveFontSize(currentFontSize);
                    }
                });
            });

            // ä¸ºæ ‡ç­¾åˆ‡æ¢æ·»åŠ å­—ä½“æ§åˆ¶å™¨çš„æ›´æ–°
            tabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    // éšè—æ‰€æœ‰å­—ä½“æ§åˆ¶å™¨
                    fontControls.forEach(control => {
                        control.classList.remove('show');
                    });
                });
            });
        });
    </script>
