    }

    // ç»‘å®šäº‹ä»¶
    $modal.find('.btn-confirm').on('click', function(e) {
      e.stopPropagation();
      closeAndResolve(true);
    });

    $modal.find('.modal-close, .btn-cancel').on('click', function(e) {
