        const themes = Array.from(themeSelector.options)
            .map(option => option.value)
            .filter(value => value && value.trim());

        if (themes.length > 0) {
            console.log(`Font Manager: ä»é¡µé¢ä¸»é¢˜é€‰æ‹©å™¨è·å–åˆ° ${themes.length} ä¸ªä¸»é¢˜:`, themes.join(', '));
            populateThemeSelect(themes);
            return;
        }
    }

    console.log('Font Manager: æœªæ‰¾åˆ°é¡µé¢ä¸»é¢˜é€‰æ‹©å™¨æˆ–é€‰æ‹©å™¨ä¸ºç©ºï¼Œå°è¯•ä»themesç›®å½•è·å–');

    // å¤‡é€‰æ–¹æ¡ˆï¼šä»themesç›®å½•è·å–ä¸»é¢˜æ–‡ä»¶åˆ—è¡¨
    fetch('./themes/')
        .then(response => response.text())
        .then(html => {
            // è§£æç›®å½•åˆ—è¡¨é¡µé¢ï¼Œæå–.jsonæ–‡ä»¶
            const parser = new DOMParser();
            const doc = parser.parseFromString(html, 'text/html');
            const links = doc.querySelectorAll('a[href$=".json"]');
            const themes = Array.from(links).map(link => {
                const href = link.getAttribute('href');
                return href.replace('.json', '');
            });
            populateThemeSelect(themes);
        })
        .catch(() => {
            // å¦‚æœæ— æ³•è¯»å–ç›®å½•ï¼Œå°è¯•æ£€æµ‹å·²çŸ¥ä¸»é¢˜æ–‡ä»¶
            console.log('Font Manager: æ— æ³•ä»themesç›®å½•è·å–ï¼Œå›é€€åˆ°æ£€æµ‹å½“å‰ä¸»é¢˜');
            detectAvailableThemes();
        });
}

function populateThemeSelect(themes) {
    const themeSelect = docContext.getElementById('bindingThemeSelect');
    if (!themeSelect) return;

    themeSelect.innerHTML = '<option value="">- é€‰æ‹©ä¸»é¢˜ -</option>';

    if (Array.isArray(themes) && themes.length > 0) {
        themes.forEach(theme => {
            const option = docContext.createElement('option');
            option.value = theme;
            option.textContent = theme;
            themeSelect.appendChild(option);
        });
        console.log(`Font Manager: æˆåŠŸåŠ è½½ ${themes.length} ä¸ªä¸»é¢˜`);
    } else {
        console.warn('Font Manager: æœªæ‰¾åˆ°ä»»ä½•ä¸»é¢˜æ–‡ä»¶');
    }
}

function detectAvailableThemes() {
    // å†æ¬¡å°è¯•ä»é¡µé¢ä¸»é¢˜é€‰æ‹©å™¨è·å–
    const themeSelector = docContext.getElementById('themes');
    if (themeSelector && themeSelector.tagName.toLowerCase() === 'select') {
        const themes = Array.from(themeSelector.options)
            .map(option => option.value)
            .filter(value => value && value.trim());

        if (themes.length > 0) {
            console.log(`Font Manager: æ£€æµ‹é˜¶æ®µä»é¡µé¢ä¸»é¢˜é€‰æ‹©å™¨è·å–åˆ° ${themes.length} ä¸ªä¸»é¢˜`);
            populateThemeSelect(themes);
            return;
        }
    }

    // æœ€åçš„å¤‡é€‰æ–¹æ¡ˆï¼šåªæ£€æµ‹å½“å‰ä¸»é¢˜æ˜¯å¦å­˜åœ¨
    const currentTheme = detectCurrentTheme();
    const availableThemes = [];

    if (currentTheme) {
        // éªŒè¯å½“å‰ä¸»é¢˜æ–‡ä»¶æ˜¯å¦çœŸå®å­˜åœ¨
        fetch(`./themes/${currentTheme}.json`, { method: 'HEAD' })
            .then(response => {
                if (response.ok) {
                    availableThemes.push(currentTheme);
                }
                populateThemeSelect(availableThemes);
            })
            .catch(() => {
                populateThemeSelect(availableThemes);
            });
    } else {
        populateThemeSelect(availableThemes);
    }
}

