const CLOUD_FONT_SITES_URL = 'https://sharkpan.xyz/f/YYmVh1/%E5%AD%97%E4%BD%93%E7%BD%91%E7%AB%99.json';

function openRecommendSitesModal() {
    ensureModalStructure();
    modalTitleElement.textContent = 'æ¨èè·å–å­—ä½“ç½‘ç«™';
    modalBodyElement.innerHTML = '';

    // æ˜¾ç¤ºåŠ è½½çŠ¶æ€
    const loadingDiv = docContext.createElement('div');
    loadingDiv.style.textAlign = 'center';
    loadingDiv.style.padding = '40px';
    loadingDiv.style.color = '#ccc';
    loadingDiv.innerHTML = 'æ­£åœ¨ä»äº‘ç«¯è·å–æ¨èç½‘ç«™æ•°æ®...';
    modalBodyElement.appendChild(loadingDiv);

    // ä»äº‘ç«¯è·å–æ•°æ®
    fetchRecommendSites()
        .then(sitesData => {
            modalBodyElement.innerHTML = '';
            renderRecommendSites(sitesData);
        })
        .catch(error => {
            console.error('è·å–æ¨èç½‘ç«™å¤±è´¥:', error);
            modalBodyElement.innerHTML = '';
        });

    _renderFooterButtons([
        { text: 'å…³é—­', className: 'font-manager-modal-button close', onClick: () => renderCombinedMainView() }
    ]);

    modalElement.style.display = 'flex';
}

function fetchRecommendSites() {
    return fetch(CLOUD_FONT_SITES_URL, {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
        }
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }
        return response.json();
    })
    .catch(error => {
        console.error('äº‘ç«¯è·å–å¤±è´¥:', error);
        throw error;
    });
