    try {
        if (state.multilangEnabled) {
            console.log(`Font Manager: å¤šè¯­è¨€æ¨¡å¼å·²å¯ç”¨ï¼Œä¸èƒ½ç›´æ¥åº”ç”¨å•ä¸€å­—ä½“ "${fontFamily}"`);
            showNotification('å½“å‰å¤„äºå¤šè¯­è¨€å­—ä½“æ¨¡å¼ï¼Œè¯·åœ¨å¤šè¯­è¨€è®¾ç½®ä¸­é…ç½®å­—ä½“ï¼Œæˆ–å…ˆå…³é—­å¤šè¯­è¨€æ¨¡å¼ã€‚');
            return;
        }

        applyGlobalFontToAllElements(fontFamily);

        localStorage.setItem(LOCAL_STORAGE_KEY_ACTIVE_FONT, fontFamily);
