function detectCurrentTheme() {
    // ä¼˜å…ˆä»SillyTavernçš„ä¸»é¢˜é€‰æ‹©å™¨è·å–
    const themeSelector = docContext.getElementById('themes');
    if (themeSelector && themeSelector.value) {
        return themeSelector.value;
    }

    // å¤‡é€‰æ–¹æ¡ˆï¼šä»CSSé“¾æ¥æ£€æµ‹
    const themeElement = docContext.querySelector('link[href*="themes/"]') || 
                        docContext.querySelector('link[href*="theme"]') ||
                        docContext.querySelector('[data-theme]');

    if (themeElement) {
        if (themeElement.href) {
            const themeName = themeElement.href.split('/').pop().replace('.css', '');
            return themeName || 'default';
        } else if (themeElement.dataset.theme) {
            return themeElement.dataset.theme;
        }
    }

    const bodyClass = docContext.body.className;
    const themeMatch = bodyClass.match(/theme-(\w+)/);
    if (themeMatch) {
        return themeMatch[1];
    }

    return 'default';
}

