function initializeFontManager() {
    try {
        const computedStyle = window.getComputedStyle(docContext.documentElement);
        const cssVarValue = computedStyle.getPropertyValue('--mainFontSize').trim();
        if (cssVarValue) {
            const parsed = parseInt(cssVarValue);
            if (!isNaN(parsed)) {
                initialMainFontSizeFromCSS = cssVarValue;
                initialMainFontSizeNumber = parsed;
                console.log('Font Manager: Initial --mainFontSize from CSS:', initialMainFontSizeFromCSS);
            }
        }
    } catch (e) {
        console.warn('Font Manager: Could not read initial --mainFontSize from CSS. Falling back to default.', e);
    }

   try {
        const computedStyle = window.getComputedStyle(docContext.documentElement);
        const cssVarValue = computedStyle.getPropertyValue('--icon-actual-size').trim();
        if (cssVarValue) {
            const parsed = parseInt(cssVarValue);
            if (!isNaN(parsed)) {
                initialIconSizeFromCSS = cssVarValue;
                initialIconSizeNumber = parsed;
                console.log('Font Manager: Initial --icon-actual-size from CSS:', initialIconSizeFromCSS);
            }
        }
    } catch (e) {
        console.warn('Font Manager: Could not read initial --icon-actual-size from CSS. Falling back to default 45px.', e);
    }

    try {
        const computedStyle = window.getComputedStyle(docContext.documentElement);
        const cssVarValue = computedStyle.getPropertyValue('--button-size').trim();
        if (cssVarValue) {
            const parsed = parseInt(cssVarValue);
            if (!isNaN(parsed)) {
                initialButtonSizeFromCSS = cssVarValue;
                initialButtonSizeNumber = parsed;
                console.log('Font Manager: Initial --button-size from CSS:', initialButtonSizeFromCSS);
            }
        }
    } catch (e) {
        console.warn('Font Manager: Could not read initial --button-size from CSS. Falling back to default 32px.', e);
    }

    loadFontsFromStorage();
    loadFontImportUrlsFromStorage();
    loadMultilangSettings();
    // åŠ è½½å­—ä½“åº”ç”¨æ¨¡å¼çŠ¶æ€
    const savedMode = localStorage.getItem('fontManagerApplicationMode');
    const savedSingleFont = localStorage.getItem('fontManagerSingleFont');
    if (savedMode) {
        state.fontApplicationMode = savedMode;
        if (savedMode === 'single' && savedSingleFont) {
            state.singleFontFamily = savedSingleFont;
        }
    }
    if (state.fontImportUrls && state.fontImportUrls.length > 0) {
        state.fontImportUrls.forEach((url, index) => {
            try {
                const styleElement = docContext.createElement('style');
                styleElement.id = `font-manager-runtime-import-${index}`;
                styleElement.setAttribute('data-font-url', url);
                styleElement.textContent = `@import url('${url}');`;
                docContext.head.appendChild(styleElement);
                console.log('Font Manager: Re-applied @import URL from storage:', url);
            } catch (e) {
                console.error('Font Manager: Error re-applying @import URL ' + url + ' from storage:', e);
            }
        });
    }

    try {
        const activeFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
        if (activeFont && state.importedFontFamilies.includes(activeFont)) {
            setTimeout(() => {
                docContext.body.style.setProperty('font-family', activeFont, 'important');
                console.log('Font Manager: Last active font applied on load:', activeFont);
            }, 500);
        } else if (activeFont) {
            localStorage.removeItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
        }
    } catch (e) {
        console.error('Font Manager: Error applying last active font from localStorage:', e);
    }

    try {
        const savedSize = localStorage.getItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_SIZE);
        if (savedSize && !isNaN(parseFloat(savedSize))) {
            applyGlobalFontSize(parseFloat(savedSize)); 
        }
    } catch (e) {
        console.error('Font Manager: Error applying last global font size from localStorage:', e);
    }

    try {
        const savedIconSize = localStorage.getItem(LOCAL_STORAGE_KEY_ICON_SIZE);
        if (savedIconSize && !isNaN(parseFloat(savedIconSize))) {
            applyIconSize(parseFloat(savedIconSize));
        }
    } catch (e) {
        console.error('Font Manager: Error applying last icon size from localStorage:', e);
    }

    try {
        const savedButtonSize = localStorage.getItem(LOCAL_STORAGE_KEY_BUTTON_SIZE);
        if (savedButtonSize && !isNaN(parseFloat(savedButtonSize))) {
            applyButtonSize(parseFloat(savedButtonSize));
        }
    } catch (e) {
        console.error('Font Manager: Error applying last button size from localStorage:', e);
    }

    Object.keys(colorSettings).forEach(key => {
        const setting = colorSettings[key];

        // åŠ è½½ä¿å­˜çš„é¢œè‰²æˆ–ä½¿ç”¨é»˜è®¤å€¼
        const savedColor = localStorage.getItem(setting.lsKey);
        if (savedColor) {
            state[key + 'Color'] = savedColor;
            applyColorSetting(key, savedColor);
            console.log(`Font Manager: åŠ è½½å·²ä¿å­˜çš„${setting.label}é¢œè‰²: ${savedColor}`);
        } else {
            state[key + 'Color'] = setting.initialValue;
            console.log(`Font Manager: ä½¿ç”¨é»˜è®¤${setting.label}é¢œè‰²: ${setting.initialValue}`);
        }
    });

    // åœ¨åº”ç”¨ä»»ä½•æ ·å¼ä¹‹å‰å…ˆè·å–çœŸæ­£çš„åˆå§‹æ ·å¼
    getInitialHljsStyles();

    // å…ˆåˆå§‹åŒ–HLJSè®¾ç½®
    initializeHljsSettings();

    // å»¶è¿Ÿåˆå§‹åŒ–æ¶ˆæ¯å†…è”ä»£ç è®¾ç½®ï¼Œç¡®ä¿ä¸ä¼šè¦†ç›–HLJS
    setTimeout(() => {
        initializeMessageInlineCodeSettings();
    }, 100);

    const savedActiveFont = localStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
    if (savedActiveFont && state.importedFontFamilies.includes(savedActiveFont)) {
        console.log('Attempting to apply saved active font:', savedActiveFont);
        setTimeout(() => {
            applyFontToBody(savedActiveFont);
        }, 700);
    } else if (savedActiveFont) {
        console.log('Saved active font not in current imported list, clearing:', savedActiveFont);
        localStorage.removeItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
    }

    injectStyles();
    waitForExtensionsMenu();

    loadLocalFontsFromIndexedDB()
        .then(() => {
            if (state.localFonts && state.localFonts.length > 0) {
                console.log(`Font Manager: ä»IndexedDBåŠ è½½äº†${state.localFonts.length}ä¸ªæœ¬åœ°å­—ä½“ï¼Œå¼€å§‹åº”ç”¨åˆ°é¡µé¢`);
                applyLocalFontsToPage();
                console.log('Font Manager: æœ¬åœ°å­—ä½“å·²ä» IndexedDB åŠ è½½å¹¶åº”ç”¨');
            } else {
                console.log('Font Manager: æ²¡æœ‰ä»IndexedDBåŠ è½½åˆ°æœ¬åœ°å­—ä½“');
            }
        })
        .catch(error => {
            console.error('Font Manager: åŠ è½½æŒä¹…åŒ–å­˜å‚¨çš„æœ¬åœ°å­—ä½“å¤±è´¥:', error);
        });

    if (state.multilangEnabled) {
        console.log('Font Manager: å¤šè¯­è¨€å­—ä½“è®¾ç½®å·²å¯ç”¨');
        applyMultilangCSS();
    } else {
        console.log('Font Manager: å¤šè¯­è¨€å­—ä½“è®¾ç½®æœªå¯ç”¨');
    }

    console.log('Font Manager initialized.');
    setupDynamicContentObserver();

    // åˆå§‹åŒ–ä¸»é¢˜é¢„è®¾
    loadThemePresets();
    loadMultilangPresets();
    loadThemeMultilangBindings();

    // å»¶è¿Ÿåˆå§‹åŒ–ä¸»é¢˜æ£€æµ‹ï¼Œç¡®ä¿é¡µé¢å…ƒç´ å®Œå…¨åŠ è½½
    setTimeout(() => {
        currentTheme = detectCurrentTheme();
        setupThemeChangeObserver();

        // æ£€æŸ¥æ˜¯å¦æœ‰å½“å‰ä¸»é¢˜çš„ç»‘å®šé¢„è®¾
        const bindingPreset = themeMultilangBindings[currentTheme];
        if (bindingPreset && multilangPresets[bindingPreset]) {
            console.log(`å‘ç°å½“å‰ä¸»é¢˜ "${currentTheme}" ç»‘å®šçš„é¢„è®¾ "${bindingPreset}"ï¼Œåˆå§‹åŒ–æ—¶åº”ç”¨å­—ä½“è®¾ç½®`);
            applyMultilangPreset(bindingPreset, false); // åˆå§‹åŒ–æ—¶ä¸æ˜¾ç¤ºè‡ªåŠ¨åˆ‡æ¢æé†’
        }
    }, 1000);
}

// ========= æ–‡æ¡£åŠ è½½å®Œæˆååˆå§‹åŒ– =========
if (document.readyState === 'loading') {
