          regex.enabled = originalEnabled;

          // éªŒè¯çŠ¶æ€ä¿ç•™
devLog(`[FilterGroup] [${operationId}] ç§»åŠ¨åçŠ¶æ€éªŒè¯ - è„šæœ¬: ${regex.script_name}, enabled: ${regex.enabled}`);

          modifiedCount++;

          movedScripts.push({
