            } else { allChatMessages = []; logDebug("No chat messages returned from API."); }
        } catch (error) { logError("è·å–èŠå¤©è®°å½•å¤±è´¥: " + error.message); console.error(error); showToastr("error", "è·å–èŠå¤©è®°å½•å¤±è´¥ã€‚"); allChatMessages = []; }
