            } else {
                $worldbookContentDisplayTextArea.val(`å½“å‰èŠå¤© (${currentChatFileIdentifier}) çš„ ${selectedSummaryType === 'small' ? 'å°æ€»ç»“' : 'å¤§æ€»ç»“'} å°šæœªç”Ÿæˆæˆ–æœªåœ¨ä¸–ç•Œä¹¦ "${currentPrimaryLorebook}" ä¸­æ‰¾åˆ°æ´»åŠ¨æ¡ç›®ã€‚`);
                // Ensure cache is fully reset if no entry is effectively shown
