/* =========================================
   18. å¼¹çª—æç¤º (Toast)
   ========================================= */
#toast-container > .toast {
    background-image: none !important;
}

div.toast {
    position: relative !important;
    padding: 18px 25px 18px 65px !important;
    background-color: #F9F9F9 !important;
