    color: #5E5E5E !important;
    opacity: 1;
}

@keyframes toastIn { 0% { transform: translateY(30px) scale(0.9); opacity: 0; } 100% { transform: translateY(0) scale(1); opacity: 1; } }
@keyframes toastOut { 0% { transform: translateY(0) scale(1); opacity: 1; } 100% { transform: translateY(-30px) scale(0.9); opacity: 0; } }
div.toast { animation: toastIn 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards; }
div.toast.toast-exit { animation: toastOut 0.4s cubic-bezier(0.6, -0.28, 0.735, 0.045) forwards; }


