/* 此主题美化仅免费发布于Discord旅程社区。请勿二传或商用！欢迎二改和调色，可使用美化编辑插件。特别感谢：Rivelle老师的月下回声主题扩展（渐变原版以此二改），尾巴、bbl、Junezz老师的CSS，以及柏柏老师的世界书布局界面美化。 */

/* 字体链接 */
@import url("https://fontsapi.zeoseven.com/285/main/result.css");

@import url("data:text/css,%40font-face%7Bfont-family%3A%22ZSFT-hd%22%3Bsrc%3Aurl(%22https%3A%2F%2Ffontsapi.zeoseven.com%2Fhd%2Fmedium.woff2%22)%20format(%22woff2%22)%2Curl(%22https%3A%2F%2Ffontsapi-storage.zeoseven.com%2Fhd%2Fmedium.woff2%22)%20format(%22woff2%22)%3Bfont-style%3Anormal%3Bfont-weight%3A500%3Bfont-display%3Aswap%3B%7D%40font-face%7Bfont-family%3A%22ZSFT-hd%22%3Bsrc%3Aurl(%22https%3A%2F%2Ffontsapi.zeoseven.com%2Fhd%2Fmain.woff2%22)%20format(%22woff2%22)%2Curl(%22https%3A%2F%2Ffontsapi-storage.zeoseven.com%2Fhd%2Fmain.woff2%22)%20format(%22woff2%22)%3Bfont-style%3Anormal%3Bfont-weight%3A400%3Bfont-display%3Aswap%3B%7D");

@import url("https://fontsapi.zeoseven.com/112/main/result.css");

:root {
    --avatar-base-border-radius: 15px;
    /* 字体名称 */
    --mainFontFamily: Noto Serif CJK;
    --monoFontFamily: Noto Serif CJK;
}

#chat {
    border: none !important;
    padding: 30px 10px 0;
}

/* 气泡 */
body.bubblechat .mes {
    margin-bottom: 50px !important;
    display: flex;
    flex-direction: column;
    position: relative;
    border: none !important;
    background-color: transparent !important;
    border-radius: var(--avatar-base-border-radius) !important;
    padding: 0 !important;

    background-image:
        linear-gradient(to bottom,
        rgba(255, 255, 255, 0.6) 0%,
        rgba(255, 255, 255, 0.6) 90%,
        rgba(196, 219, 255, 0.8) 100%) !important;

    box-shadow: 0 4px 5px rgba(154, 172, 220, 0.5);
    z-index: 0;
    backdrop-filter: blur(10px) !important;
    -webkit-backdrop-filter: blur(10px) !important;
}

/* 气泡边框 */
.mes::after {
    content: "";
    position: absolute;
    inset: 0;
    z-index: 1;
    border-radius: var(--avatar-base-border-radius);
    /* 边框宽度 */
    padding: 1px;

    background: linear-gradient(to bottom, rgba(196, 227, 255, 0.8), rgba(168, 208, 255, 0.8));

    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
}

/* 头像容器 */
.mesAvatarWrapper {
    height: 250px !important; /* 图片高度 */
    width: 100% !important;
    min-width: 100% !important;
    position: relative;
    overflow: visible !important;
    order: -1;
    display: flex;
    align-items: flex-start;
    margin: 0 !important;
    padding: 0 !important;
    background: transparent !important;
    box-shadow: none !important;
}

/* 头像 */
.mesAvatarWrapper .avatar {
    position: absolute;
    top: 0;
    left: 0;
    width: 100% !important;
    height: 100% !important;
    margin: 0 !important;
    z-index: 0;

    /* 渐变遮罩 */
    -webkit-mask-image: linear-gradient(to bottom, black 30%, transparent 100%);
    mask-image: linear-gradient(to bottom, black 30%, transparent 100%);
    pointer-events: none !important;
}

/* 头像图片 */
.mesAvatarWrapper .avatar img {
    /* 调整聊天界面图片显示效果 */
    /* 1为原图比例，+放大，-缩小，可写小数点 */
    --img-scale: 1;
    /* 水平位移，0%为原图最左边部分 */
    --img-x: 0%;
    /* 垂直位移，0%为原图顶端部分 */
    --img-y: 0%;

    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
    object-position: var(--img-x) var(--img-y) !important;
    transform: scale(var(--img-scale));
    transform-origin: var(--img-x) var(--img-y);
    transition: transform 0.3s ease;
    border: none !important;
}

/* 小标题样式 */
.mesAvatarWrapper::before {
    content: "";
    position: absolute;
    top: -10px; /* 位置 */
    left: 0;
    z-index: 9;

    /* 框的大小 */
    width: 235px;
    height: 20px;

    /* 渐变背景 */
    background-image:
        linear-gradient(105deg,
        rgba(255, 255, 255, 1) 0%,
        rgba(255, 255, 255, 1) 32%,
        rgba(199, 214, 255, 1) 32%,
        rgba(206, 232, 255, 1) 90%,
        transparent 90%);

    border-left: 5px solid rgba(184, 210, 250, 1);
}

.mesIDDisplay::before {
    content: "SPECIAL";
    font-weight: bold;
    letter-spacing: 1px;
    margin: 0 10px 0 10px;
    font-family: "DYmingA";
    font-style: italic;

    /* 字体渐变 */
    background-image: linear-gradient(to bottom, rgba(181, 193, 255, 1) 30%, rgba(111, 183, 235, 1) 100%) !important;

    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
}

/* 谢谢bbl（鞠躬） */
.mes_timer:not(:empty)::before {
    content: "-";
}
.mes .tokenCounterDisplay:not(:empty)::before {
    content: "-";
}

/* 三元素 */
.mes .mes_timer, .mes .mesIDDisplay, .mes .tokenCounterDisplay {
    position: relative;
    z-index: 10;
    margin-top: -7px;
    font-size: 0.9em !important;
    opacity: 1 !important;
    font-weight: 300 !important;
    font-style: italic;
    font-family: "DYmingA";
    color: rgba(145, 163, 204, 1);
}

.mes_block {
    padding: 20px 10px 0;
    position: relative;
    overflow: visible !important;
}

/* 名字容器 */
.mes_block .ch_name {
    position: absolute;
    top: -50px;
    left: 0;
    width: 100%;
    z-index: 5;
    padding: 0 20px;
    box-sizing: border-box;
    align-items: flex-end;
    flex-direction: row-reverse !important;
}

.mes .ch_name > .flex1 {
    justify-content: flex-end !important;
    text-align: right !important;
}

.mes .ch_name .alignItemsBaseline {
    flex-direction: row-reverse !important;
}

/* 名字 */
.mes .name_text {
    position: relative !important;
    display: inline-block !important;
    font-weight: normal;
    font-size: 0.8em;
    font-family: sans-serif;
    color: rgba(255, 255, 255, 1);
    letter-spacing: 3px;
    padding: 0 10px;
    background-image:
        linear-gradient(to right,
        rgba(167, 183, 217, 1) 0%,
        rgba(174, 192, 222, 1) 37%,
        rgba(183, 216, 255, 1) 37%,
        rgba(185, 204, 255, 1) 90%,
        transparent 90%);
}

/* Forget me Not */
.mes .name_text::after {
    content: "Forget me Not";
    position: absolute;
    top: -50px;
    right: 0;
    font-family: "ZSFT-hd";
    font-size: 3em;
    white-space: nowrap;
    font-style: italic;

    /* 烫金渐变 */
    background-image: linear-gradient(
        135deg,
        rgba(255, 255, 255, 1) 0%,
        rgba(104, 185, 220, 1) 25%,
        rgba(40, 96, 179, 1) 50%,
        rgba(121, 181, 236, 1) 75%,
        rgba(28, 102, 170, 1) 100%
    );

    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;

    filter: drop-shadow(0 1px 1px rgba(255, 255, 255, 0.5));
}

/* YOURS */
.mes .name_text::before {
    content: "YOURS//";
    display: inline-block;
    font-size: 0.7em;
    font-weight: normal;
    font-family: "DYmingA";
}

/* 操作按钮 */
.mes_buttons {
    position: absolute;
    top: 25px;
    left: 20px;
    font-size: 14px;
}

.mes_buttons, .extraMesButtons {
    place-items: center;
    background: linear-gradient(
        135deg,
        rgba(168, 202, 242, 1) 0%,
        rgba(179, 203, 230, 1) 25%,
        rgba(199, 211, 236, 1) 50%,
        rgba(165, 182, 225, 1) 75%,
        rgba(139, 201, 228, 1) 100%
    );    
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
}

.mes_button, .extraMesButtons>div {
    filter: none !important;
    opacity: 1 !important;
}

.mes .fa-ellipsis:before, .mes .fa-star:before {
    content: "▶";
}

.mes .fa-camera:before, .mes .fa-pencil:before {
    content: "▷";
}

/* 主要文本编辑按钮 */
.mes_reasoning_actions .edit_button, .mes_edit_buttons {
    filter: none !important;
}

.mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
    opacity: 1 !important;
}

.mes_reasoning_actions {
    margin: 0 -100px !important;
}

/* 思维链 */
.mes_reasoning_details {
    place-items: center !important;
    margin-right: 0;
}

.mes_reasoning_details .mes_reasoning_summary {
    justify-content: center !important;
    align-items: center !important;
    position: relative !important;
    width: 120px;
    height: 25px;
    margin: 30px 0 10px;
    text-align: center;
    background: linear-gradient(to bottom,
        rgba(255, 255, 255, 0) 0%,
        rgba(235, 237, 255, 0.6) 50%,
        rgba(224, 237, 255, 0.6) 50%,
        rgba(255, 255, 255, 0) 100%) !important;
}

.mes_reasoning_details .mes_reasoning_summary::before {
    content: "·MEMORIES·\A再见爱人";
    position: absolute;
    font-family: DYmingA;
    font-weight: 900;
    font-size: 0.7em;
    letter-spacing: 3px;
    line-height: 1.5;

    background-image: linear-gradient(to right,
        rgba(211, 221, 255, 1) 0%,
        rgba(110, 151, 255, 1) 20%,
        rgba(150, 166, 255, 1) 50%,
        rgba(114, 188, 255, 1) 70%,
        rgba(191, 232, 255, 1) 100%
    );

    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    white-space: pre-wrap;
}

.mes_reasoning_details .mes_reasoning_arrow {
    display: none;
}

.mes_reasoning_header_title {
    font-size: 0;
}

.mes_reasoning_header {
    margin: 0 !important;
    padding: 0 !important;
    background-color: rgba(0, 0, 0, 0) !important;
    position: absolute;
    top: -50px;
    left: 50%;
    transform: translateX(-50%) rotate(-20deg);
}

.mes_reasoning_header::before {
    content: "؛༊";
    color: rgba(157, 184, 255, 1);
    font-size: 1.5em;
    text-shadow: 0 0 10px rgba(153, 199, 255, 1) !important;
    transition: transform 0.3s ease;
}

.mes_reasoning_header:hover::before {
    transform: scale(1.2);
}

.mes_reasoning_actions {
    margin: 0 -50px;
}

.mes_reasoning, .mes_text pre code, .mes_text blockquote {
    border-radius: 0 !important;
    border: none;
    border-top: 2px solid rgba(200, 212, 255, 1);
    border-bottom: 2px solid rgba(189, 223, 255, 1);
    padding: 10px;
    margin: 20px 10px;
    outline: 1px solid rgba(199, 218, 255, 0.7);
    outline-offset: 5px;
    color: var(--SmartThemeBodyColor);
}

.mes_text {
    padding: 0 5px 10px !important;
    text-align: justify;
}

/* 引用文字 */
.mes q {
    padding-right: 1px;
    font-weight: bold;
    background-image: linear-gradient(120deg,
        rgba(101, 149, 221, 1) 0%,
        rgba(92, 145, 224, 1) 50%,
        rgba(95, 120, 187, 1) 90%,
        rgba(181, 181, 181, 1) 100%
    );
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    filter:
        drop-shadow(0 0 2px rgba(225, 231, 255, 1)) /* 白色微光 */
        drop-shadow(0 0 8px rgba(231, 235, 255, 1)); /* 粉色朦胧扩散 */
}

/* 斜体/行内代码文本 */
.mes_text em, code:not(pre code.hljs) {
    background-color: transparent;
    color: var(--SmartThemeEmColor);
    border: none;
    font-style: italic;
    padding: 0 6px;
    border-radius: 15px;
    background-image: linear-gradient(
        90deg,
        rgba(255, 255, 255, 0) 0%,
        rgba(174, 216, 255, 0.3) 50%,
        rgba(255, 255, 255, 0) 100%
    );
    border-bottom: 1px solid rgba(190, 193, 255, 0.3);
}

/* 导航栏与底栏 */
#send_form, body.no-blur #send_form, #top-bar, body.no-blur #top-bar {
    outline: none !important;
    border: none !important;
    backdrop-filter: blur(10px) !important;
    -webkit-backdrop-filter: blur(10px) !important;
    background-color: rgba(255, 255, 255, 0.7) !important;
    box-shadow: 0 0 20px 5px rgba(196, 221, 255, 1);
}

#send_form, body.no-blur #send_form {
    border-radius: 20px 20px 0 0;
}

#top-settings-holder .drawer-icon:before {
    font-size: 15px !important;
    content: "୨ৎ";
}

#top-settings-holder .drawer-icon.openIcon:before {
    content: "❄" !important;
    font-size: 18px !important;
}

#rightSendForm>div, #leftSendForm>div, #rightSendForm > div > [class*="fa-"], #rightSendForm > div > i, #quickPersona {
    font-size: 18px !important;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
    opacity: 1;
}

#send_form .mes_stop {
    padding: 0px 10px;
    opacity: 1;
}

#send_textarea, #send_textarea:focus {
    clip-path: none !important;
    border: none !important;
    backdrop-filter: none !important;
}

/* 谢谢尾巴的输入框提示词自定义 */
#nonQRFormItems {
    position: relative;
}

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important;
}

#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "shh... what did the flower say";
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-style: italic;
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
    font-family: Georgia;
    font-weight: 200;
    color: rgba(129, 178, 222, 1);
}

#nonQRFormItems {
    margin: 10px 5px;
}

/* 输入框换行 */
@media (max-width: 768px) {
    #nonQRFormItems {
        display: grid;
        grid-template-columns: auto 1fr auto;
        grid-template-rows: auto auto;
        place-items: center;
    }

    #leftSendForm {
        grid-row: 1 / 2;
        grid-column: 1 / 2;
    }

    #rightSendForm {
        grid-row: 1 / 2;
        grid-column: 3 / 4;
    }

    #send_textarea:focus {
        grid-row: 2 / 3;
        grid-column: 1 / 4;
        justify-self: center;
        width: 97%;
        margin: 0px 0px -5px;
    }
}

#form_sheld {
    margin: 0 auto !important;
}

#sheld {
    top: var(--bottomFormBlockSize);
    height: calc(100dvh - var(--bottomFormBlockSize));
    max-height: calc(100dvh - var(--bottomFormBlockSize));
    min-height: calc(100dvh - var(--bottomFormBlockSize));
}

#top-bar::after {
    bottom: 0;
}
#form_sheld {
    position: relative;
}
#form_sheld::before {
    top: 0;
}

#top-bar::after, #form_sheld::before {
    content: "";
    position: absolute;
    background: linear-gradient(
        90deg,
        transparent 0%,
        rgba(152, 181, 255, 0.5) 10%,
        rgba(205, 219, 255, 1) 50%,
        rgba(155, 183, 255, 0.5) 90%,
        transparent 100%);
    width: 100%;
    height: 2px; /* 线条高度 */
    z-index: 100;
    pointer-events: none;
}

/* 切换消息 */
#chat .mes:last-of-type {
    margin-bottom: 70px !important;
    padding-bottom: 50px !important;
}

.swipe_right, .swipe_left {
    opacity: 0.8 !important;
    bottom: 20px;
    font-size: 1em !important;
}

.swipeRightBlock {
    bottom: -10px !important;
    width: 100%;
    display: flex;
    justify-content: center;
}

.swipes-counter {
    opacity: 1 !important;
    font-size: 0.8em !important;
    letter-spacing: 3px;
    font-weight: 900;
    position: absolute;
    bottom: 35px;
}

.swipes-counter::before, .swipes-counter::after {
    content: '—';
}

.swipe_right {
    position: absolute;
    right: 20px;
    transform: translateY(-40%);
}

/* 菜单 */
#left-nav-panel, #completion_prompt_manager_popup, #rm_api_block, #AdvancedFormatting, #WorldInfo, #user-settings-block, #Backgrounds, #rm_extensions_block, .drawer-content.openDrawer, #right-nav-panel,
body.no-blur .popup, body.no-blur #bulk_tag_popup, body.no-blur #dialogue_popup,
#options, #extensionsMenu, .popup .popper-modal,
body.no-blur * .select2-dropdown {
  border: none !important;
  padding: 10px;
  border-radius: 0px;
  box-shadow: none;
  backdrop-filter: blur(3px) !important;
    -webkit-backdrop-filter: blur(3px) !important;
  background-image:
        linear-gradient(to bottom,
        rgba(255, 255, 255, 1) 0%,
        rgba(234, 244, 255, 1) 90%,
        rgba(208, 225, 255, 1) 100%) !important;
  background-color: transparent !important;
}

/* 图标样式 */
.fa, .fa-brands, .fa-classic, .fa-regular:not(.fa-star), .fa-sharp, .fa-solid:not(.fav_on, .world_set, .fa-power-off, .fa-chevron-right, .fa-chevron-left, .mes .fa-solid), .fab, .far, .fas {
  color: rgba(169, 192, 226, 1) !important;
  font-size: 15px !important;
  filter: none;
  opacity: 1;
}

#amily2_drawer_icon.closedIcon {
  opacity: 1 !important;
}

/* 图标按钮 */
.menu_button {
    color: var(--SmartThemeBodyColor);
    filter: none;
    background-color: transparent !important;
    border: none !important;
    background: radial-gradient(ellipse closest-side, rgba(189, 206, 255, 0.65) 0, transparent 100%);
    padding: 7px;
    box-shadow: inset 0 -1px 5px rgba(206, 231, 255, 1) !important;
}

/* 输入框 */
input:not(input[type="range"], input[type='checkbox']), select, .text_pole, .textarea_compact, textarea:not(.select2-search__field),
#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item .qr--content .qr--set-itemMessage,
#extensionSideBar #extensionSideBarContainer .sideBarItem.selected,
.select_chat_block[highlight] {
    background-color: transparent !important;
    border-radius: var(--avatar-base-border-radius) !important;
    border: 1px solid rgba(183, 200, 227, 0.7) !important;
    box-shadow: inset 0 0 10px 3px rgba(208, 224, 255, 0.7);
    padding: 5px 10px;
}

select:focus-visible, input:focus-visible, textarea:not(#send_textarea):focus-visible {
    outline: 1px solid rgba(196, 213, 255, 1);
    outline-offset: -5px;
}

/* 标题 */
strong::before, b::before, h4:not(.margin0) span::before, #persona-management-button.drawer .standoutHeader::before {
  content: "✧₊⁺";
  margin-right: 5px;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader, .inline-drawer-header {
  font-family: "点点像素体-菱形";
  border: none;
  background-image: linear-gradient(to bottom, rgba(132, 165, 229, 1) 30%, rgba(151, 193, 241, 1) 100%) !important;
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
  font-weight: 900;
  font-size: 1.1em;
}

/* Spacing */
#anchor_checkbox label, #power-user-option-checkboxes label, .checkbox_label, .auto_continue_settings_block {
  margin: 7px 0px;
}

/* 勾选框 */
#anchor_checkbox label, #power-user-option-checkboxes label, .checkbox_label, .auto_continue_settings_block {
    place-items: flex-start;
}

input[type="checkbox"] {
    background-color: transparent;
    border: none;
    box-shadow: none;
    outline: none;
    filter: none;
    width: 1.2em;
    height: 1.2em;
    cursor: pointer;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    vertical-align: middle;
    margin: 0 5px;
    position: relative;
}

input[type="checkbox"]::after {
    content: "♡";
    font-size: 1.2em;
    line-height: 1;
    transition: all 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

input[type="checkbox"]:checked::after {
    content: "♥";
    color: rgba(176, 203, 255, 0.8);
}

input[type="checkbox"]:hover::after {
    color: white;
    text-shadow: 0 0 10px rgba(203, 224, 255, 1);
}

input[type="checkbox"]::before {
    display: none;
}

input[type="range"]::-webkit-slider-thumb {
  border: none;
  box-shadow: none;
}

/* 选择栏勾选框 */
.select2-results__option--selected.select2-results__option::before {
    content: "♥";
    line-height: normal !important;
}

.select2-results__option::before {
    content: "♡";
    border: none !important;
    margin-top: -10px !important;
}

/* 滑动条 */
input[type="range"], input[type="range"]:disabled {
    -webkit-appearance: none;
    appearance: none;
    filter: none;
    width: 100%;
    cursor: pointer;
    height: 6px !important;
    background: rgba(255, 255, 255, 0) !important;
    border-radius: 15px;
    outline: none !important;
    box-shadow: none !important;
    overflow: hidden;
    border: 1px solid rgba(172, 210, 255, 1);
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 25px;
    height: 15px;
    border-radius: 0;
    cursor: pointer;
