custom_css:
/*
    主题: 懒大王の秋日花园
    作者: 懒大王 & gemini
    描述: 一个温暖的橙白配色主题，拥有精致的贴纸和非对称布局。
*/

/* 0. 全局样式与变量 */
:root {
    --theme-orange: #F2A46B; /* 主题橙色 */
    --theme-orange-light: rgba(242, 164, 107, 0.1); /* 主题浅橙色 (用于背景) */
}

/* 1. 消息容器与布局 */
.mes {
    display: flex !important;
    flex-direction: column;
    align-items: center;
    max-width: 95%;
    margin: 110px auto 0 auto !important; 
    padding: 0 0 0px 0 !important;
    position: relative;
    background: transparent !important;
    overflow: visible !important; 
}
.mes_block {
    position: relative;
    padding: 40px 15px 55px 15px;
    margin-top: 10px !important;
    width: 100%;
    border-radius: 10px;
    border: 2px solid var(--theme-orange) !important; /* <--- 配色 */
    background-image: none !important; 
    background-color: #ffffff !important;
}

/* 2. 头像与装饰 */
.mesAvatarWrapper {
    position: absolute !important;
    top: -60px !important;
    right: 12% !important;
    left: auto !important;
    transform: translateX(50%);
    width: auto !important;
    margin: 0 !important;
    text-align: center;
    z-index: 3;
    pointer-events: none !important;
}
.mesAvatarWrapper .avatar {
    position: relative;
    width: 50px !important;
    height: 50px !important;
    margin: 0 auto 12px !important;
    z-index: 1;
}
.mes .avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 50%;
    border: none !important;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    position: relative;
    z-index: 0;
}
.mesAvatarWrapper::before {
    content: '';
    position: absolute;
    background-image: url('https://s1.imagehub.cc/images/2025/10/26/1bf4df0c5eec9a90b3d1065807d0255d.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 190px;
    aspect-ratio: 558 / 312;
    z-index: -1;
    top: 50%;
    left: 50%;
    transform: translate(-80%, -68%);
    pointer-events: none;
}

/* 3. 气泡外部贴纸 */
.mes:not(.smallSysMes)::before {
    content: '';
    position: absolute;
    top: -55px;
    left: 5px;
    width: 150px;
    aspect-ratio: 479 / 256;
    background-image: url('https://s1.imagehub.cc/images/2025/10/26/e54e1ff0acbbfc174444eda002290a47.png');
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 5;
    pointer-events: none;
}
.mes:not(.smallSysMes)::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 50%;
    transform: translateX(-50%);
    width: 300px;
    aspect-ratio: 1074 / 268;
    background-image: url('https://s1.imagehub.cc/images/2025/10/26/2fd29b27c1585ab9491fd172e1e7454b.png');
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 7;
    pointer-events: none;
}

/* 4. 信息铭牌 */
.mes .mesIDDisplay, 
.mes .tokenCounterDisplay,
.mes .mes_timer {
    position: absolute !important;
    top: 85px !important;
    bottom: auto !important;
    font-size: 0.7em;
    color: var(--theme-orange) !important; /* <--- 配色 */
    border: 1px dashed var(--theme-orange) !important; /* <--- 配色 */
    border-radius: 5px;
    padding: 2px 5px;
    background-color: var(--theme-orange-light) !important; /* <--- 配色 */
    z-index: 4;
    white-space: nowrap;
}
.mes .mesIDDisplay { 
    right: 60px !important;
    left: auto !important; 
}
.mes .tokenCounterDisplay { 
    right: 140px !important;
    left: auto !important; 
}
.mes .mes_timer { 
    right: 220px !important;
    left: auto !important; 
}

/* 5. 气泡内部内容 */
.mes .name_text,
.mes .timestamp {
    float: left;
    margin-bottom: 10px;
}
.mes .name_text {
    font-size: 16px !important;
    font-weight: bold;
    /* 颜色已移除，将跟随ST主文本颜色设置 */
}
.mes .timestamp {
    margin-left: 8px !important;
    font-size: 10px !important;
    padding-top: 3px;
    /* 颜色已移除，将跟随ST时间戳颜色设置 */
}
.mes .mes_buttons {
    float: right;
}
.mes_text {
    width: 100% !important;
    padding: 0 !important;
    margin: 0 !important;
    box-sizing: border-box !important;
    clear: both;
}

/* 6. 其他修正 */
.last_mes .mesAvatarWrapper {
    padding-bottom: 0px !important;
}

/* --- 7. 思维链样式 --- */

/* 思维链头部容器 */
.mes_reasoning_header {
    display: inline-block !important; /* 让它像一个标签而不是一整行 */
    position: relative !important;   /* 设为“画板”，用于定位贴纸 */
    margin-top: 10px !important;      /* 与上方内容的间距 */
    margin-bottom: 10px !important;   /* 与下方内容的间距 */
    margin-left: 5px !important;     /* 向内缩进一点，避免贴边 */

    /* 留出左边距给贴纸，防止文字和贴纸重叠 */
    padding: 6px 15px 6px 40px !important; 

    /* 使用主题配色 */
    background-color: var(--theme-orange-light) !important;
    border: 1px dashed var(--theme-orange) !important;
    border-radius: 8px !important;

    cursor: pointer;
    transition: all 0.2s ease;
}

/* 隐藏默认的文字和箭头图标 */
.mes_reasoning_header_title,
.mes_reasoning_arrow {
    display: none !important;
}

/* 使用伪元素创建我们自己的标题 */
.mes_reasoning_header::before {
    content: '秋日的思绪...' !important;
    color: var(--theme-orange) !important;
    font-weight: bold;
    font-size: 0.9em;
}

/* 添加装饰贴纸 */
.mes_reasoning_header::after {
    content: '' !important;
    position: absolute;
    width: 45px;  /* 控制贴纸的大小 */
    height: 45px;
    background-image: url('https://s1.imagehub.cc/images/2025/10/26/a242e1e204650612515b9fc56722f5be.png');
    background-size: contain;
    background-repeat: no-repeat;

    /* 定位与旋转，让它“盖”在左上角 */
    top: -15px;
    left: -12px;
    transform: rotate(-15deg);

    z-index: 1; /* 确保它在“便签”上方 */
}

/* 添加悬停交互效果 */
.mes_reasoning_header:hover {
    border-style: solid;
    box-shadow: 0 2px 8px rgba(242, 164, 107, 0.4);
    transform: translateY(-2px);
}

/* --- 8. 顶部与底部栏 (直接链接版) --- */

/* 8.1. 顶部栏样式 */
#st-header, #top-bar {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
    height: 60px !important;
}
#st-header::before, #top-bar::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100px !important;
    z-index: -1;
    background: url('https://s1.imagehub.cc/images/2025/10/26/26d3cd52fe01bec226ba6abce8175d81.png') no-repeat center top !important;
    background-size: contain !important;
    pointer-events: none;
}

/* 8.2. 顶部图标 */
#top-settings-holder .drawer-icon {
    font-size: 0 !important;
    color: transparent !important;
    width: 25px !important;
    height: 25px !important;
    background-size: contain !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    margin: 0 2px !important;
    padding: 4px !important;
    transition: transform 0.2s ease;
    opacity: 1 !important;
}
#top-settings-holder .drawer-icon:hover {
    transform: scale(1.15);
}
#top-settings-holder .drawer-icon::before {
    content: none !important;
}

/* 8.3. 为每个顶部图标直接分配图片链接 */
#leftNavDrawerIcon { background-image: url('https://img.baidu.re/i/2025/10/soq0tp.png') !important; }
#sys-settings-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/soowlt.png') !important; }
#advanced-formatting-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/sopjs1.png') !important; }
#WI-SP-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/soq6je.png') !important; }
#user-settings-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/sopcb9.png') !important; }
#table_drawer_icon.drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/soolr2.png') !important; }
#extensions-settings-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/sopqlh.png') !important; }
#persona-management-button .drawer-icon { background-image: url('https://img.baidu.re/i/2025/10/soo75e.png') !important; }
#logo_block .drawer-icon { background-image: url('https://s1.imagehub.cc/images/2025/10/26/8e47ab67550d00c07b9beaa387408968.png') !important; }
#backgrounds-button .drawer-icon { background-image: url('https://s1.imagehub.cc/images/2025/10/26/8e47ab67550d00c07b9beaa387408968.png') !important; }
#rightNavDrawerIcon { background-image: url('https://img.baidu.re/i/2025/10/sop0v0.png') !important; }


/* 8.4. 发送栏样式 */
#send_form {
    align-items: center !important;
    width: calc(100% - 20px) !important;
    margin: 0 auto 10px auto !important;
    padding: 5px 10px !important;
    border: 2px dashed var(--theme-orange) !important;
    border-radius: 20px !important;
    min-height: auto !important;
    background: rgba(255, 255, 255, 0.7) !important;
    backdrop-filter: blur(4px) !important;
}
#send_textarea {
    background: transparent !important;
    color: #333 !important;
    border: none !important;
    box-shadow: none !important;
}
#send_textarea::placeholder {
    color: var(--theme-orange) !important;
    opacity: 0.7;
}

/* 8.5. 底部图标 */
#send_but,
#stscript_stop.active,
#options_button,
#extensionsMenuButton {
    font-size: 0 !important;
    color: transparent !important;
    width: 36px !important;
    height: 36px !important;
    border: none !important;
    background-color: transparent !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    background-size: contain !important;
    transition: transform 0.2s ease !important;
    margin: 0 2px !important;
    opacity: 1 !important;
}
#send_but::before,
#stscript_stop.active::before,
#options_button::before,
#extensionsMenuButton::before {
    content: none !important;
}
#send_but:hover,
#stscript_stop.active:hover,
#options_button:hover,
#extensionsMenuButton:hover {
    transform: scale(1.1);
}

/* 8.6. 为每个底部图标直接分配图片链接 */
#send_but { background-image: url('https://img.baidu.re/i/2025/10/sooigb.png') !important; }
#stscript_stop.active { background-image: url('https://img.baidu.re/i/2025/10/sop0v0.png') !important; }
#options_button { background-image: url('https://img.baidu.re/i/2025/10/soowlt.png') !important; }
#extensionsMenuButton { background-image: url('https://img.baidu.re/i/2025/10/sop0v0.png') !important; }


/* 8.7. QR快捷栏 (悬浮显示) */
#qr--bar {
    max-height: 0;
    opacity: 0;
    overflow: hidden;
    transition: max-height 0.3s ease, opacity 0.3s ease;
}
#send_form:hover #qr--bar {
    max-height: 40px;
    opacity: 1;
}

/* --- 9. 设置面板与表单控件 (直接链接版) --- */

/* 9.1. 面板容器 (抽屉背景) */
.drawer-content {
  border-radius: 5px !important;
  background-color: rgba(255, 250, 245, 1) !important; 
}

/* 9.2. 可折叠的抽屉标题 */
:is(#extensions_settings, #extensions_settings2, #rm_api_block, #left-nav-panel) .inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  position: relative !important;
  background: var(--theme-orange-light) !important;
  border: 1px dashed var(--theme-orange) !important;
  color: #A56A3A !important;
  padding: 5px 15px !important;
  border-radius: 10px !important;
  transition: all 0.2s ease;
}
:is(#extensions_settings, #extensions_settings2, #rm_api_block, #left-nav-panel) .inline-drawer-header:hover,
#user-settings-block h4:hover,
.standoutHeader:hover {
  border-style: solid;
  background-color: var(--theme-orange) !important;
  color: white !important;
}

/* 9.2a. 特殊标题样式 (例如“主题颜色”) */
.sub_block_title {
    display: inline-block !important;
    background: var(--theme-orange-light) !important;
    border: 1px dashed var(--theme-orange) !important;
    color: #A56A3A !important;
    padding: 4px 12px !important;
    border-radius: 15px !important;
    margin: 5px 0 !important;
    font-weight: bold;
}

/* 9.3. 折叠图标 (小箭头) */
.inline-drawer-icon {
  font-size: 0 !important;
  color: transparent !important;
  background-image: url('https://s1.imagehub.cc/images/2025/10/26/8e47ab67550d00c07b9beaa387408968.png') !important;
  background-size: contain !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  width: 22px !important;
  height: 22px !important;
  transition: transform 0.3s ease !important;
}
.inline-drawer-icon::before, .inline-drawer-icon::after {
  content: none !important;
}
.inline-drawer-toggle.open .inline-drawer-icon {
  transform: rotate(90deg) !important;
}

/* 9.4. 表单控件 */
/* 通用输入框 (文本、数字) */
textarea, input[type="text"], input[type="number"] {
  background-color: rgba(255, 255, 255, 0.8) !important;
  border: 1px dashed var(--theme-orange) !important;
  border-radius: 6px !important;
  padding: 8px !important;
  color: #A56A3A !important;
  transition: all 0.2s ease-in-out;
}

/* 9.4a. 通用（浅色）下拉选择框 */
select {
  -webkit-appearance: none !important; appearance: none !important;
  background-image: url('https://img.baidu.re/i/2025/10/soq0tp.png') !important;
  background-repeat: no-repeat !important;
  background-position: right 10px center !important;
  background-size: 18px !important;
  padding-right: 40px !important;
  background-color: rgba(255, 255, 255, 0.8) !important;
  border: 1px dashed var(--theme-orange) !important;
  border-radius: 6px !important;
  color: #A56A3A !important;
}

/* 9.5. 特殊（深色）下拉菜单 */
#themes,
#ui_language_select {
    background-image: url('https://img.baidu.re/i/2025/10/sopqlh.png') !important;
    background-size: 16px !important;
    padding-right: 35px !important;
    background-color: rgba(230, 210, 173, 0.6) !important;
    border: 1px solid var(--theme-orange) !important;
    border-radius: 4px !important;
}

/* 输入框、选择框获得焦点时的效果 */
textarea:focus, input[type="text"]:focus, input[type="number"]:focus, select:focus {
  border-color: var(--theme-orange) !important;
  border-style: solid;
  box-shadow: 0 0 8px rgba(242, 164, 107, 0.5) !important;
}

/* 9.6. 滑块 */
input[type="range"] {
  -webkit-appearance: none !important; appearance: none !important;
  width: 100% !important; height: 6px !important;
  background: var(--theme-orange-light) !important;
  border-radius: 3px !important;
  outline: none !important;
  cursor: pointer !important;
  margin: 25px 0 !important;
}
input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none !important; appearance: none !important;
  width: 45px !important; height: 45px !important;
  background-image: url('https://s1.imagehub.cc/images/2025/10/26/0e753ec02807178207bb3978a2b53f0d.png') !important;
  background-size: contain !important;
  background-color: transparent !important;
  border: none !important;
  cursor: pointer !important;
  transition: transform 0.2s ease;
}
input[type="range"]:hover::-webkit-slider-thumb {
  transform: scale(1.1);
}

/* 9.7. 复选框 (勾选框) */
input[type="checkbox"] {
  -webkit-appearance: none !important; appearance: none !important;
  width: 14px !important;
  height: 14px !important;
  border-radius: 4px !important;
  border: 1px dashed var(--theme-orange) !important;
  background-color: rgba(255, 255, 255, 0.8) !important;
  position: relative !important;
  cursor: pointer !important;
  vertical-align: -3px;
  transition: all 0.2s ease;
}
input[type="checkbox"]:checked {
  background-color: var(--theme-orange-light) !important;
  border-style: solid !important;
}
input[type="checkbox"]:checked::after {
  content: '' !important;
  position: absolute !important;
  top: 0; left: 0;
  width: 100%; height: 100%;
  background-image: url('https://img.baidu.re/i/2025/10/sopqlh.png') !important;
  background-size: 80%;
  background-position: center;
  background-repeat: no-repeat;
}

/* 9.8. 通用按钮 */
button, .menu_button {
  background: var(--theme-orange-light) !important;
border: 1px solid var(--theme-orange) !important;
  color: #A56A3A !important;
  transition: all 0.2s ease !important;
}
button:hover, .menu_button:hover {
  background: var(--theme-orange) !important;
  color: white !important;
  transform: translateY(-2px);
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}
/* --- 9.9. 世界书 (World Info) --- */

/* 每个世界书条目的样式 */
.world_entry {
  margin: 8px 0 !important; /* 上下间距，让条目分开 */
  padding: 10px 15px !important; /* 内部边距，让文字不贴边 */
  border: 1px dashed var(--theme-orange) !important; /* 你的主题色虚线边框 */
  border-radius: 10px !important; /* 圆角 */
  background-color: rgba(242, 164, 107, 0.1) !important; /* 淡淡的橙色背景 */
  position: relative; /* 为了定位伪元素图标 */
  overflow: hidden; /* 防止图标溢出太多 */
  transition: all 0.2s ease;
}

/* 鼠标悬浮在条目上时的效果 */
.world_entry:hover {
  border-style: solid;
  background-color: rgba(242, 164, 107, 0.2) !important;
}

/* 右上角的装饰图标 */
.world_entry::before {
  content: '';
  position: absolute;
  top: -5px;
  right: -5px;
  width: 40px;
  height: 40px;
  background-image: url('https://img.baidu.re/i/2025/10/soolr2.png'); /* 书本图标 */
  background-size: contain;
  background-repeat: no-repeat;
  opacity: 0.2; /* 图标淡一点，不抢眼 */
  transform: rotate(15deg);
  pointer-events: none; /* 确保图标不影响鼠标操作 */
}

/* 条目中“关键词”的样式 */
.world_entry_key {
  font-weight: bold;
  color: var(--theme-orange) !important;
  background-color: rgba(255, 255, 255, 0.5) !important;
  padding: 2px 6px;
  border-radius: 4px;
  margin-right: 5px;
}

/* --- 9.10. 预设面板 (Prompt Manager) --- */

/* 预设列表的整体背景 */
#completion_prompt_manager #completion_prompt_manager_list {
    background: transparent !important; /* 让列表背景透明，以显示抽屉的背景色 */
}

/* 每个预设条目的基础样式 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    position: relative; /* 为了定位装饰图标 */
    background: rgba(255, 255, 255, 0.7) !important;
    border: 1px dashed var(--theme-orange) !important;
    border-radius: 10px !important;
    padding: 8px 15px !important;
    margin-bottom: 6px !important;
    transition: all 0.2s ease;
    overflow: hidden; /* 防止图标溢出 */
}

/* 鼠标悬浮在条目上时的效果 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:hover {
    transform: scale(1.02);
    border-style: solid;
}

/* 选中（激活）的条目样式 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt.selected {
    background: var(--theme-orange-light) !important;
    border-color: var(--theme-orange) !important;
    border-style: solid;
}

/* 禁用状态的条目样式 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
    background-color: rgba(230, 230, 230, 0.5) !important;
    border-color: rgba(200, 200, 200, 0.8) !important;
    opacity: 0.6;
}
/* 禁用条目的文字颜色 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name * {
    color: #999 !important;
    -webkit-text-fill-color: #999 !important;
}

/* 右上角的装饰图标 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt::after {
    content: '';
    position: absolute;
    top: -5px;
    right: -5px;
    width: 30px;
    height: 30px;
    background-image: var(--icon-sparkles); /* 使用你的星星图标 */
    background-size: contain;
    background-repeat: no-repeat;
    opacity: 0.2;
    transform: rotate(15deg);
    pointer-events: none;
}
/* 禁用的条目不显示装饰图标 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled::after {
    display: none !important;
}

/*正则*/
.regex-script-container {
    margin-right: 45px;
}

