custom_css:
/* ===== 字体 ===== */
@font-face {
    font-family: "Jing Hua ";
    src: url("https://files.catbox.moe/138mza.ttf") format("truetype");
}
body, #nonQRFormItems textarea,.mes_text pre code, .mes_reasoning pre code,input, select, button {
    font-family: "Jing Hua ", sans-serif;
}

code {
    background-color: rgb(207 225 224 / 53%);
    color: rgb(132 113 102);
}
/* 斜体 */
.mes_text em {
    text-decoration: underline;
    text-decoration-color: #d0bfb7c2;
    text-decoration-thickness: 2px;
}

/* 引用 */
.mes_text q {
    background-color: rgb(185 226 226 / 40%);
    border-radius: 15px;
    padding: 0px 0px;
    display: inline;
    box-decoration-break: clone;
    -webkit-box-decoration-break: clone;
}
/* 代码块 */
.mes_text pre,
.mes_reasoning pre {
background: url('https://i.postimg.cc/NjGfSHm6/IMG-2450.png') repeat;
background-size: 40px 100px;
padding: 15px 10px 10px;
border-radius: 15px;
margin: 15px 0 0;
position: relative;
box-sizing: border-box;
}
.mes_text pre::after,
.mes_reasoning pre::after {
  content: "";
  position: absolute;
  top: -15px;
  left: 50%;
  transform: translateX(-50%);
  width: 30px;
  height: 30px;
  background: url('https://i.postimg.cc/9M274fsY/IMG-2451.png') no-repeat center;
  background-size: contain;
}

.mes_text pre code, .mes_reasoning pre code {
    background-color: #f3fffd;
    color: #705843;
    box-sizing: border-box;
    border-radius: 12px;
}

#chat > div > div.mes_block > div.mes_text > pre > code > i {
  font-size: 0; /* 隐藏原图标内容 */
  display: inline-block;
  width: 20px;
  height: 20px;
  background: url('https://i.postimg.cc/NFDRTCVm/IMG-2459.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}

/*引用块*/
.mes_text blockquote, .mes_reasoning blockquote {
    background-color: rgb(161 153 143 / 10%);
    border-radius: 5px;
}
/* ===== 动画 ===== */
/* 移除原始伪元素 */
#load-spinner ,#load-spinner::before{
   animation: none;
   content: "";
}

/* 创建新的自定义加载动画 */
#load-spinner::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background: url('https://i.postimg.cc/jqc8yMyJ/IMG-2462.png') center/contain no-repeat;
    transform: translate(-50%, -50%);
    z-index: 1000;
    /* 添加左右旋转摇晃动画 */
    animation: rotate-shake 3s ease-in-out infinite;
}

/* 左右旋转摇晃动画关键帧 */
@keyframes rotate-shake {
    0%, 100% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    25% {
        transform: translate(-50%, -50%) rotate(-15deg);
    }
    50% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    75% {
        transform: translate(-50%, -50%) rotate(15deg);
    }
}

/* 隐藏原有旋转动画 */
@keyframes custom-spin {
    display: none;
}
/* ===== 思维块美化 ===== */
.mes_reasoning_header {
    cursor: pointer;
    position: relative;
    left: 50%;
    transform: translate(-50%, -50%);
    user-select: none;
    margin: 5px;
    padding: 2px 8px;
    padding-right: calc(0.7em + 14px);
    background-color: #dff8f7;
    border: 2px dashed #ba967d;
    border-radius: 20px;
    font-size: calc(var(--mainFontSize) * 0.9);
    color: #8a5c2b;
    align-items: baseline;
    font-size: 0 !important;
}

.mes_reasoning_header::before {
    content: "今日食谱揭秘"; /* 修改文字内容 */
    font-size: calc(var(--mainFontSize) * 0.9);
    display: inline;
}

.fa-chevron-up:before {
    content: ""; /* 去除原有样式 */
}

.fa-chevron-up::before {
    content: "";
    display: inline-block;
    width: 10px;
    height: 10px;
    background-image: url("https://i.postimg.cc/qqMj9rXR/IMG-2455.png");
    background-size: 10px 10px;
    background-repeat: no-repeat;
}

/* ====== 气泡 ====== */


.mes_block {
    padding-left: 0;
}

body.bubblechat .mes, body.bubblechat .mes[is_user="true"] {
    padding: 30px;
    border-radius: 0px;
    margin-bottom: 5px;
    border: 1px solid var(--SmartThemeBorderColor);
    max-width: 570px;
    margin: 350px auto 95px auto;
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://i.postimg.cc/8PC4tysJ/IMG-2445.png");
    background-size: 570px 171px;
    background-repeat: repeat;
    background-position: center;
}

.mes {
    --swipeCounterHeight: 15px;
    --swipeCounterMargin: 5px;
    display: flex;
    align-items: flex-start;
    padding: 30px 30px 0;
    margin: 350px auto 95px auto;
    width: 570px;
    max-width: 570px;
    color: var(--SmartThemeBodyColor);
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://i.postimg.cc/8PC4tysJ/IMG-2445.png");
    background-size: 570px 171px;
    background-repeat: repeat;
    background-position: center;
}

.last_mes {
    margin-bottom: 0;
}

/* 为所有气泡添加顶部图片 */
.mes::before {
    content: "";
    position: absolute;
    top: -342px; /* 图片高度 */
    width: 570px;
    height: 342px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://i.postimg.cc/rpYg6xH7/IMG-2448.png");
    background-size: 570px 342px; 
    background-repeat: no-repeat;
    background-position: center;
    z-index: 0;
}

/* 为所有气泡添加底部图片 */
.mes::after {
    content: "";
    position: absolute;
    bottom: -76px; 
    width: 570px;
    height: 76px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://i.postimg.cc/bY409zXM/IMG-2447.png");
    background-size: 570px 76px; 
    background-repeat: no-repeat;
    background-position: center;
    z-index: 1;
}

/* 翻页 */
.swipe_left {
    left: 80px;
    bottom:-45px;
}
.swipe_right {
    position: absolute;
    right: 80px;
    bottom:-45px;
}

.fa-chevron-right:before {
    right: 50px;
    bottom:-30px;
    content: "";
    display: inline-block;
    width: 35px;
    height: 35px;
    background-image: url("https://i.postimg.cc/TwZ4F1YJ/IMG-2458.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

.fa-chevron-left:before {
    content: "";
    display: inline-block;
    width: 35px;
    height: 35px;
    background-image: url("https://i.postimg.cc/TwZ4F1YJ/IMG-2458.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

/* 文本 */
.mes_text {
    padding-left: 5px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-right: 5px;
}

/*幽灵*/
.fa-ghost:before {
  content: "";
  display: inline-block;
  width: 25px;
  height: 25px;
  background: url('https://i.postimg.cc/G254C0Zk/IMG-2463.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}

.reasoning_edit_textarea, .edit_textarea {
    background-color: rgb(226 242 237 / 52%);
}
/* ====== 头像 ====== */
.mesAvatarWrapper {
    width: 170px;
    height: 170px;
    object-fit: cover;
    display: block;
    position: absolute;
    left: 350px;
    top: -208px; /* 微调位置使头像底部高于消息15px */
}

.mesAvatarWrapper .avatar img {
width: 340%;
height: 340%;
object-fit: cover;
border-radius: inherit;
}

/* 覆盖图片 */
.mesAvatarWrapper::after {
    content: ""; /* 确保伪元素有内容 */
    position: absolute;
    left: 50%;
    top: 50%; /* 添加垂直居中定位 */
    transform: translate(-50%, -50%); /* 同时调整水平和垂直偏移 */
    width: 185px;
    height: 185px;
    background-image: url("https://i.postimg.cc/HnhQf2KZ/IMG-2442.png");
    background-size: cover;
    background-position: center;
    z-index: 2; /* 覆盖头像 */
    pointer-events: none; /* 防止影响点击 */
}

.mes .mes_timer {
    cursor: default;
    opacity: 1;
    font-size: calc(var(--mainFontSize) * 1);
    font-weight: 600;
    text-align: right;
    position: absolute;
    right: 200px;
    white-space: nowrap; /* 防止换行 */
    top: 125px;
    z-index: 10;
}


.mes .mesIDDisplay {
    cursor: default;
    opacity: 1;
    font-size: calc(var(--mainFontSize) * 1);
    font-weight: 600;
    text-align: right;
    position: absolute;
    white-space: nowrap; /* 防止换行 */
    right: 200px;
    top: 95px;    
    z-index: 10;
}


.mes .tokenCounterDisplay {
    cursor: default;
    opacity: 1;
    font-size: calc(var(--mainFontSize) *1);
    font-weight: 600;
    text-align: right;
    position: absolute;
    right: 200px; 
    text-align: right; /* 右对齐 */
    white-space: nowrap; /* 防止换行 */
    top: 67px;
    z-index: 10;
}
.mes .tokenCounterDisplay::before {
    content: " ……";
    display: inline;
}

/* 时间戳位置 */
.mes .timestamp {
    font-size: calc(var(--mainFontSize) * 1);
    font-weight: 600;
    position: absolute; /* 绝对定位 */
    right: 50%; 
    top: -50px; /* Y轴偏移-40px */
    text-align: right; /* 右对齐 */
    white-space: nowrap; /* 防止换行 */
    z-index: 10; /* 确保显示在最前面 */
    opacity: 1; /* 保持原有透明度 */
}



.mes .name_text {
    font-size: calc(var(--mainFontSize) * 1.2);
    position: absolute; /* 绝对定位 */
    left: 50px;
    top: -193px; /* Y轴向上偏移165px */
    text-align: center; 
    white-space: nowrap; /* 防止换行 */
    z-index: 10; /* 确保显示在最前面 */
    cursor: default;
    opacity: 1;
    font-weight: 600;
}

.mes_block {
  transform: none !important;
}
/* ===== 底部输入栏 ===== */
#qr--bar {
    outline: none;
    margin: 0;
    transition: 0.3s;
    opacity: 0.7;
    display: flex;
    align-items: center;
    justify-content: center;
    top:5px ;
    width: 100%;
    max-width: 100%;
    /* 修改高度为自适应 */
    height: auto;
    /* 设置最大高度并允许垂直滚动 */
    max-height: 30px; /* 保留原始高度作为最大高度 */
    /* 允许水平和垂直方向滚动 */
    overflow: auto; /* 替代原来的overflow-x和overflow-y */
    scrollbar-width: thin;
    scrollbar-color: #888 #f0f0f0;
}

/* 统一滚动条样式 */
#qr--bar::-webkit-scrollbar {
    width: 4px;  /* 垂直滚动条宽度 */
    height: 4px; /* 水平滚动条高度 */
}

#qr--bar::-webkit-scrollbar-track {
    background: #f0f0f0;
}

#qr--bar::-webkit-scrollbar-thumb {
    background-color: #888;
    border-radius: 2px;
}

#send_form {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    width: 100%;
    margin: 0px auto 0px auto; /* 上下边距15px */
    border: 1px solid var(--SmartThemeBorderColor);
    border-radius: 0; /* 四个角的圆角度数为0 */
    backdrop-filter: blur(var(--SmartThemeBlurStrength));
    position: relative; /* 为内勾边定位做准备 */
}

#nonQRFormItems {
    padding: 5px;
    border: 0;
    position: relative;
    background-image: url("https://i.postimg.cc/90nGvpSv/IMG-2440.png"); /* 添加背景图片 */
    background-size: 90px 30px; /* 设置背景图片大小 */
    background-repeat: repeat; /* 平铺 */
    background-position: center;
    display: flex;
    flex-direction: row;
    column-gap: 5px;
    font-size: var(--bottomFormIconSize);
    order: 25;
    width: 100%;
    min-height: 30px;
    align-items: center;
    overflow: hidden;
}
#nonQRFormItems textarea {
    flex: 1;
    min-height: 30px;
    max-height: 100px;
    resize: none;
    overflow-y: auto;
    line-height: normal;
    padding: 5px;
    margin: 0;
    border: none;
    outline: none;
    background: rgb(110 72 55 / 83%) !important;
}

/* 字体图标容器固定大小 */
#nonQRFormItems .icon-container {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 30px;
    width: 30px; /* 固定宽度 */
    flex-shrink: 0; /* 防止图标被压缩 */
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button{
    transform: scale(0.9);
}


/*停止钮*/
.fa-circle-stop:before,
.fa-stop-circle:before {
  content: ""; /* 移除原图标 */
  display: inline-block;
  width: 30px;
  height: 30px;
  background: url('https://i.postimg.cc/jqc8yMyJ/IMG-2462.png') no-repeat center;
  background-size: contain;
  opacity: 1;
  animation: rotateShake 1.5s ease-in-out infinite; /* 增加持续时间到1.5秒 */
  transform-origin: center; /* 设置旋转中心 */
}

@keyframes rotateShake {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(-10deg); /* 向左旋转 */
  }
  75% {
    transform: rotate(10deg); /* 向右旋转 */
  }
}

/*小飞机*/
.fa-paper-plane:before {
  content: "";
  display: inline-block;
  width: 30px;
  height: 30px;
  background: url('https://i.postimg.cc/jqc8yMyJ/IMG-2462.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}


/* ===== 顶部 ===== */

#top-bar {
    margin: 0 auto;
    left: 0;
    right: 0;
    display: inline-block;
    height: 30px; /* 固定高度 */
    position: absolute;
    background-image: url("https://i.postimg.cc/90nGvpSv/IMG-2440.png"); /* 背景图片 */
    box-shadow: none;
    background-size: 90px 30px; /* 图片大小 */
    background-repeat: repeat; /* 平铺 */
    z-index: 3005;
}

.drawer-toggle .drawer-icon {
    transform-origin: center;
    display: inline-block;
    width: auto;
    height: auto;
}

.fa-user-cog:before, .fa-user-gear:before,
.fa-sliders-h:before,
 .fa-sliders:before,
.fa-font:before,
.fa-user:before,
.fa-cubes:before,
.fa-address-card:before,
.fa-contact-card:before,
.fa-vcard:before {
  content: "";
  display: inline-block;
  width: 25px;
  height: 25px;
  background: url('https://i.postimg.cc/jqc8yMyJ/IMG-2462.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}

.fa-panorama:before,.fa-bars-progress:before, .fa-tasks-alt:before,.fa-plug:before,.fa-atlas:before, .fa-book-atlas:before,.fa-image:before,.fa-face-smile:before, .fa-smile:before {
  content: "";
  display: inline-block;
  width: 25px;
  height: 25px;
  background: url('https://i.postimg.cc/jqc8yMyJ/IMG-2462.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}
/* ===== UI ===== */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    background: -webkit-linear-gradient(right, #addcd3e6, #cdfff387);
    opacity: 0.7;
    margin-bottom: 4px;
    padding: 2px 5px 2px 5px;
    border: 2px solid rgb(111 100 73);
    position: relative;
    border-radius: 20px;
    box-shadow: none !important;
}

/* 滑块 */

input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 5px;
    background: var(--white50a);
    border-radius: 15px;
    background-size: 70% 100%;
    background-repeat: no-repeat;
    cursor: ew-resize;
    /* 移除可能的阴影 */
    box-shadow: none;
}

/* Webkit浏览器滑块样式 */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    background: url('https://i.postimg.cc/NFDRTCVm/IMG-2459.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

/* Firefox浏览器滑块样式 */
input[type="range"]::-moz-range-thumb {
    width: 20px;
    height: 20px;
    background: url('https://i.postimg.cc/NFDRTCVm/IMG-2459.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

/* 针对IE浏览器（如果需要） */
input[type="range"]::-ms-thumb {
    box-shadow: none;
}

/* 图标 */

.fa-bars:before, .fa-navicon:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 30px;
    height: 30px;
    background: url('https://i.postimg.cc/3JJDDBZ9/IMG-2465.png') center/contain no-repeat;
    vertical-align: middle;
}

.fa-magic-wand-sparkles:before, .fa-wand-magic-sparkles:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 30px;
    height: 30px;
    background: url('https://i.postimg.cc/Zn9t1XSD/IMG-2466.png') center/contain no-repeat;
    vertical-align: middle;
}

.drawer-content select {
    background-color: #c3b8b3;
    border-radius: 10px;
}

.neo-range-input, #completion_prompt_manager #completion_prompt_manager_list {
    background-color: #adcac761;
}

textarea, .textarea_compact, .range-block-counter input, .text_pole {
    background-color: #685b57;
    color: rgb(194 205 205);
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #b7b7b7;
}

#completion_prompt_manager .completion_prompt_manager_header div {
    color: #918a86;
}

#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    filter:none;
}

/*预设正则空间预留*/
#global_scripts_block ,#scoped_scripts_block ,#completion_prompt_manager_list{
    padding-right: 30px;
}

/*热切换*/
#HotSwapWrapper {
    position: relative;
    overflow-x: auto;
    scrollbar-width: thin; /* Firefox */
    scrollbar-color: rgba(255, 182, 193, 0.3) 
}

#HotSwapWrapper::-webkit-scrollbar {
    height: 1px; /* 设置滚动条高度为3px */
}

#HotSwapWrapper::-webkit-scrollbar-track {
    background: transparent; /* 滚动条轨道透明 */
}

#HotSwapWrapper::-webkit-scrollbar-thumb {
    scrollbar-color: rgba(255, 182, 193, 0.3) 
    border-radius: 0; /* 无圆角 */
    border: none; /* 无边框 */
}

#HotSwapWrapper > * {
    flex-shrink: 0;
}



/* --- 默认隐藏 QR 按钮容器，并设置平滑过渡 --- */
.qr--buttons {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  transform: translateY(20px);
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.5s ease,
    transform 0.5s ease;
}

/* 感谢twelve.老师分享的隐藏qr和dengdeng老师分享的隐藏弹窗，感谢nini老师进行的整合TT爱大家…… */
#send_form:focus-within .qr--buttons {
  opacity: 1;
  visibility: visible;
  max-height: 120px;
  transform: translateY(0);
  pointer-events: auto;
}

#qr--bar {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.3s ease;
}

#send_form:focus-within #qr--bar,
.qr--buttons.active #qr--bar {
  opacity: 1;
  visibility: visible;
  max-height: 200px;
  pointer-events: auto;
}


/* 全局滑动条样式 */
::-webkit-scrollbar {
    width: 1px; /* 滑动条宽度 */
    height: 1px; /* 水平滑动条高度 */
}

::-webkit-scrollbar-track {
    background: transparent; /* 轨道背景透明 */
}

::-webkit-scrollbar-thumb {
    background: rgba(188,166, 153, 0.5); /* 半透明浅粉色 */
    border-radius: 0; /* 无圆角 */
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(188,166, 153, 0.5); /* 鼠标悬停时稍微加深 */
}

/* Firefox 浏览器 */
* {
    scrollbar-width: thin; /* 细滚动条 */
    scrollbar-color: rgba(188,166, 153, 0.5) transparent; /* 滑块半透明浅粉色，轨道透明 */
}

/* 分割线 */
hr {
    background-image: url("https://i.postimg.cc/tJMhL5wp/IMG-2439.png");
    background-size: 100px 25px; /* 设置图片大小 */
